"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";
import useColombianLocations from "@/app/hooks/useColombianLocations";
import useBasicMetadata from "@/app/hooks/useBasicMetadata";
import useImageTypes from "@/app/hooks/useImageTypes";
import { useUploadImages } from "@/app/hooks/useUploadImages";
import { useCreateProperty } from "@/app/hooks/useCreateProperty";
import { useInsertPropertyMedia } from "@/app/hooks/useInsertPropertyMedia";
import ProgressBar from "@/app/ui/properties/createProperty/progressBar";
import BasicPropertyForm from "@/app/ui/properties/createProperty/basicPropertyForm";
import LocationPropertyForm from "@/app/ui/properties/createProperty/locationPropertyForm";
import FeaturesPropertyForm from "@/app/ui/properties/createProperty/featuresPropertyForm";
import DetailsPropertyForm from "@/app/ui/properties/createProperty/detailsPropertyForm";
import ImagesPropertyForm from "@/app/ui/properties/createProperty/imagesPropertyForm";
import { PropertyData } from "@/lib/definitios";

export default function CreatePropertyPage() {
  const [propertyData, setPropertyData] = useState<Partial<PropertyData>>({});
  const [currentStep, setCurrentStep] = useState(0);
  const totalSteps = 5;
  const router = useRouter();

  const locations = useColombianLocations();

  const { metadata, loading, error } = useBasicMetadata();
  const { imagesTypes } = useImageTypes();
  const { createProperty } = useCreateProperty();
  const { uploadImages, uploadStatus } = useUploadImages();
  const { insertPropertyMedia } = useInsertPropertyMedia();

  const handleUpdateProperty = (updatedData: Partial<PropertyData>) => {
    setPropertyData((prevData) => ({
      ...prevData,
      ...updatedData,
    }));
  };

  const handleNextStep = () => {
    if (currentStep < totalSteps - 1) {
      setCurrentStep((prevStep) => prevStep + 1);
    }
  };

  const handlePreviousStep = () => {
    if (currentStep > 0) {
      setCurrentStep((prevStep) => prevStep - 1);
    }
  };

  const handleSubmit = (data: Partial<PropertyData>) => {
    handleUpdateProperty(data);
    handleNextStep();
  };

  const handleFinalSubmit = async (
    images: File[][],
    imageTypes: {
      type: string;
      id: number;
      category: "imageType" | "commonArea";
    }[]
  ) => {
    try {
      const propertyId = await createProperty(propertyData);

      if (!propertyId) {
        throw new Error("No se pudo crear la propiedad.");
      }

      const result = await uploadImages(propertyId, images, imageTypes);

      if (result) {
        const { urlsMatrix, typesArray } = result;
        await insertPropertyMedia(propertyId, urlsMatrix, typesArray);
      } else {
        throw new Error("Error al subir las imágenes.");
      }

      router.push("/admin/propiedades");
    } catch (err) {
      console.error("Error durante el proceso:", err);
    }
  };

  if (loading) return <p>Cargando datos...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="container mx-auto p-6">
      <h1 className="mt-24 lg:mt-20 text-3xl text-center font-semibold mb-10 text-primary dark:text-primaryLight">
        Agregar Propiedad
      </h1>

      <div className="mb-10 text-center mx-auto">
        <ProgressBar currentStep={currentStep} />
      </div>

      <div className="mt-6">
        {currentStep === 0 && metadata && (
          <BasicPropertyForm
            formData={propertyData || {}}
            onChange={handleUpdateProperty}
            onSubmit={handleSubmit}
            onPrevious={handlePreviousStep}
            onNext={handleNextStep}
            categories={metadata.categories}
            propertyTypes={metadata.propertyTypes}
            currentStep={currentStep}
            totalSteps={totalSteps}
          />
        )}

        {currentStep === 1 && (
          <LocationPropertyForm
            formData={propertyData || {}}
            onChange={handleUpdateProperty}
            onSubmit={handleSubmit}
            onPrevious={handlePreviousStep}
            onNext={handleNextStep}
            currentStep={currentStep}
            totalSteps={totalSteps}
            locations={locations}
          />
        )}

        {currentStep === 2 && metadata && (
          <div>
            <FeaturesPropertyForm
              formData={propertyData || {}}
              onChange={handleUpdateProperty}
              onSubmit={handleSubmit}
              onPrevious={handlePreviousStep}
              onNext={handleNextStep}
              currentStep={currentStep}
              totalSteps={totalSteps}
              housingTypes={metadata.housingTypes}
            />
          </div>
        )}

        {currentStep === 3 && metadata && (
          <div>
            <DetailsPropertyForm
              formData={propertyData || {}}
              onChange={handleUpdateProperty}
              onSubmit={handleSubmit}
              onPrevious={handlePreviousStep}
              onNext={handleNextStep}
              currentStep={currentStep}
              totalSteps={totalSteps}
              commonAreas={metadata.commonAreas}
              nearbyServices={metadata.nearbyServices}
            />
          </div>
        )}

        {currentStep === 4 && (
          <div>
            <ImagesPropertyForm
              formData={propertyData}
              onChange={handleUpdateProperty}
              onSubmit={handleFinalSubmit}
              onPrevious={handlePreviousStep}
              onNext={handleNextStep}
              currentStep={currentStep}
              totalSteps={totalSteps}
              imagesTypes={imagesTypes}
            />
          </div>
        )}
      </div>

      <div className="text-center mt-4">
        <button
          className="bg-secondary text-white px-4 py-2 rounded-md hover:bg-secondaryLight transition-colors dark:bg-secondaryDark dark:hover:bg-secondaryLight"
          onClick={() => router.push("/admin/propiedades")}
        >
          Volver
        </button>
      </div>
    </div>
  );
}

membresias.page.tsx:
"use client";

import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import axios from "axios";
import Table from "@/app/ui/properties/table";
import MembershipModal from "@/app/ui/properties/memberships/membershipModal";
import { Membership } from "@/lib/definitios";
import { useDebouncedCallback } from "use-debounce";

export default function MembershipsPage() {
  const [memberships, setMemberships] = useState<Membership[]>([]);
  const [totalEntries, setTotalEntries] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [entriesPerPage, setEntriesPerPage] = useState(7);
  const [searchTerm, setSearchTerm] = useState("");
  const [modalOpen, setModalOpen] = useState(false);
  const router = useRouter();

  const [updateMembership, setUpdateMembership] = useState({
    id: 0,
    name: "",
    benefits: "",
    price: 0,
    propertiesFeatured: 0,
    maxProperties: 0,
    maxImagesProperty: 0,
    discountThreeMonths: 0,
    discountSixMonths: 0,
    discountTwelveMonths: 0,
  });

  const [errors, setErrors] = useState({
    nameError: "",
    benefitsError: "",
    priceError: "",
    discountThreeMonthsError: "",
    discountSixMonthsError: "",
    discountTwelveMonthsError: "",
    maxPropertiesError: "",
    maxImagesError: "",
  });

  const debouncedSearch = useDebouncedCallback((term: string) => {
    setSearchTerm(term);
    console.log(term);
    setCurrentPage(1);
  }, 300);

  const fetchMemberships = async () => {
    try {
      const response = await axios.get(
        `/api/properties/metadata/memberships?page=${currentPage}&pageSize=${entriesPerPage}&searchTerm=${searchTerm}`
      );
      setMemberships(response.data.memberships);
      setTotalEntries(response.data.totalEntries);
    } catch (error) {
      console.error("Error fetching memberships:", error);
    }
  };

  useEffect(() => {
    fetchMemberships();
  }, [currentPage, entriesPerPage, searchTerm]);

  const validateFields = () => {
    let isValid = true;
    let tempErrors = {
      nameError: "",
      benefitsError: "",
      priceError: "",
      discountThreeMonthsError: "",
      discountSixMonthsError: "",
      discountTwelveMonthsError: "",
      maxPropertiesError: "",
      maxImagesError: "",
    };

    if (!updateMembership.name.trim()) {
      tempErrors.nameError = "El nombre es obligatorio.";
      isValid = false;
    }

    if (!updateMembership.benefits.trim()) {
      tempErrors.benefitsError =
        "La descripción de los beneficios es obligatoria.";
      isValid = false;
    }

    if (updateMembership.price <= 0) {
      tempErrors.priceError = "El precio debe ser mayor a 0.";
      isValid = false;
    }

    if (updateMembership.discountThreeMonths <= 0) {
      tempErrors.discountThreeMonthsError =
        "El porcentaje de descuento debe ser mayor a 0.";
      isValid = false;
    }

    if (updateMembership.discountSixMonths <= 0) {
      tempErrors.discountSixMonthsError =
        "El porcentaje de descuento debe ser mayor a 0.";
      isValid = false;
    }

    if (updateMembership.discountTwelveMonths <= 0) {
      tempErrors.discountTwelveMonthsError =
        "El porcentaje de descuento debe ser mayor a 0.";
      isValid = false;
    }

    if (updateMembership.maxProperties <= 0) {
      tempErrors.maxPropertiesError = "Debe haber al menos 1 propiedad.";
      isValid = false;
    }

    if (updateMembership.maxImagesProperty <= 0) {
      tempErrors.maxImagesError = "Debe haber al menos 1 imagen.";
      isValid = false;
    }

    setErrors(tempErrors);
    return isValid;
  };

  const resetErrors = () => {
    setErrors({
      nameError: "",
      benefitsError: "",
      priceError: "",
      discountThreeMonthsError: "",
      discountSixMonthsError: "",
      discountTwelveMonthsError: "",
      maxPropertiesError: "",
      maxImagesError: "",
    });
  };

  const handleEditClick = (membership: Membership) => {
    setUpdateMembership(membership);
    setModalOpen(true);
  };

  const handleUpdate = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateFields()) {
      return;
    }

    try {
      await axios.put("/api/properties/metadata/memberships", updateMembership);
      setModalOpen(false);
      fetchMemberships();
    } catch (error) {
      console.error("Error actualizando la membresía:", error);
    }
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;

    setUpdateMembership({
      ...updateMembership,
      [name]: value,
    });

    console.log(updateMembership);
  };

  const goToNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const goToPreviousPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 1));
  };

  const goToPage = (pageNumber: number) => {
    setCurrentPage(pageNumber);
  };

  const handleEntriesPerPageChange = (
    e: React.ChangeEvent<HTMLSelectElement>
  ) => {
    setEntriesPerPage(Number(e.target.value));
    setCurrentPage(1);
  };

  const headers: { label: string; key: keyof Membership }[] = [
    { label: "Membresía", key: "name" },
    { label: "Beneficios", key: "benefits" },
    { label: "Precio", key: "price" },
    { label: "Propiedades Destacadas", key: "propertiesFeatured" },
    { label: "Max. Propiedades", key: "maxProperties" },
    { label: "Max. Imágenes por Propiedad", key: "maxImagesProperty" },
    { label: "Descuento por 3 Meses", key: "discountThreeMonths" },
    { label: "Descuento por 6 Meses", key: "discountSixMonths" },
    { label: "Descuento por 12 Meses", key: "discountTwelveMonths" },
  ];

  return (
    <div className="container mx-auto mt-24 pb-10">
      <h1 className="text-3xl font-bold mb-6 text-center text-primary dark:text-primaryLight pb-5">
        Gestión de Membresías
      </h1>

      <div className="px-4 lg:px-8 py-5 bg-backgroundLight dark:bg-secondaryLight rounded-3xl lg:mx-4 shadow-lg">
        <Table
          data={memberships.filter((membership) => membership.id !== 1004)}
          headers={headers}
          totalEntries={totalEntries}
          entry="membresías"
          currentPage={currentPage}
          totalPages={Math.ceil(totalEntries / entriesPerPage)}
          goToPage={goToPage}
          goToNextPage={goToNextPage}
          goToPreviousPage={goToPreviousPage}
          entriesPerPage={entriesPerPage}
          handleEntriesPerPageChange={handleEntriesPerPageChange}
          handleSearchChange={(e) => debouncedSearch(e.target.value)}
          canDelete={false}
          onEditClick={handleEditClick}
        />
      </div>

      <MembershipModal
        show={modalOpen}
        onClose={() => {
          setModalOpen(false);
          resetErrors();
        }}
        onSubmit={handleUpdate}
        handleChange={handleChange}
        membership={updateMembership}
        errors={errors}
      />

      <button
        onClick={() => router.push("/admin/propiedades")}
        className="px-4 py-2 mt-4 bg-primary text-white rounded-lg hover:bg-primaryDark transition-colors dark:bg-primary dark:hover:bg-primaryDark dark:text-backgroundLight mx-auto block"
      >
        Volver
      </button>
    </div>
  );
}

