                </div>

                {passwordError && (
                  <div className="text-red-500 text-sm flex items-center gap-2 mt-1">
                    <AiOutlineExclamationCircle className="w-5 h-5" />
                    {passwordError}
                  </div>
                )}
              </div>

              {emptyFieldError && (
                <div className="text-red-500 text-sm flex items-center gap-2 mt-1">
                  <AiOutlineExclamationCircle className="w-5 h-5" />
                  {emptyFieldError}
