            />
          </div>
        </div>

        <StepNavigationButtons
          currentStep={currentStep}
          totalSteps={totalSteps}
          onPrevious={onPrevious}
          onNext={handleNext}
        />
      </form>
    </div>
  );
}

createProperty.imagesPropertyForm.tsx:
"use client";

import { useState, ChangeEvent, useEffect } from "react";
import { PropertyData, ImageType } from "@/lib/definitios";
import {
  AiOutlineClose,
  AiOutlineExclamationCircle,
  AiOutlineDown,
} from "react-icons/ai";
import StepNavigationButtons from "../../stepNavigationButtons";

type UploadImagesFormProps = {
  formData: PropertyData;
  onChange: (updatedData: Partial<PropertyData>) => void;
  onPrevious: () => void;
  onNext: () => void;
  currentStep: number;
  totalSteps: number;
  imagesTypes: ImageType[];
  onSubmit: (
    imagesMatrix: File[][],
    imageTypesArray: {
      type: string;
      id: number;
      category: "imageType" | "commonArea";
    }[]
  ) => void;
};

export default function ImagesPropertyForm({
  formData,
  onPrevious,
  onNext,
  currentStep,
  totalSteps,
  imagesTypes,
  onSubmit,
}: UploadImagesFormProps) {
  const [images, setImages] = useState<Record<string, File[]>>({});
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [expandedSections, setExpandedSections] = useState<
    Record<string, boolean>
  >({});

  useEffect(() => {
    const initialExpandedState: Record<string, boolean> = {};
    [...imagesTypes, ...(formData.commonAreas || [])].forEach((item) => {
      initialExpandedState[item.name] = false;
    });
    setExpandedSections(initialExpandedState);
  }, [imagesTypes, formData]);

  const toggleSection = (category: string) => {
    setExpandedSections((prev) => ({
      ...prev,
      [category]: !prev[category],
    }));
  };

  const handleImageChange = (
    category: string,
    event: ChangeEvent<HTMLInputElement>,
    maxImages: number
  ) => {
    const selectedFiles = Array.from(event.target.files || []);
    const currentImages = images[category] || [];

    if (currentImages.length + selectedFiles.length > maxImages) {
      setErrors((prev) => ({
        ...prev,
        [category]: `Máximo ${maxImages} imágenes permitidas para esta categoría.`,
      }));
      return;
    }

    setImages((prev) => ({
      ...prev,
      [category]: [...currentImages, ...selectedFiles],
    }));
    setErrors((prev) => ({ ...prev, [category]: "" }));
  };

  const handleRemoveImage = (category: string, index: number) => {
    const updatedImages = [...(images[category] || [])];
    updatedImages.splice(index, 1);
    setImages((prev) => ({ ...prev, [category]: updatedImages }));
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    imagesTypes
      .filter((type) => type.isRequired)
      .forEach((item) => {
        if (!images[item.name] || images[item.name].length === 0) {
          newErrors[
            item.name
          ] = `Debe subir al menos una imagen para ${item.name}.`;
        }
      });

    formData.commonAreas?.forEach((area) => {
      if (!images[area.name] || images[area.name].length === 0) {
        newErrors[
          area.name
        ] = `Debe subir al menos una imagen para ${area.name}.`;
      }
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const transformImages = () => {
    const imagesMatrix: File[][] = [];
    const imageTypesArray: {
      type: string;
      id: number;
      category: "imageType" | "commonArea";
    }[] = [];

    Object.entries(images).forEach(([type, files]) => {
      imagesMatrix.push(files);

      const imageType = imagesTypes.find((imgType) => imgType.name === type);
      const commonArea = formData.commonAreas?.find(
        (area) => area.name === type
      );

      if (imageType) {
        imageTypesArray.push({
          type: imageType.name,
          id: imageType.id,
          category: "imageType",
        });
      } else if (commonArea) {
        imageTypesArray.push({
          type: commonArea.name,
          id: commonArea.id,
          category: "commonArea",
        });
      }
    });

    return { imagesMatrix, imageTypesArray };
  };

  const handleSubmit = () => {
    if (validateForm()) {
      const { imagesMatrix, imageTypesArray } = transformImages();
      onSubmit(imagesMatrix, imageTypesArray);
      onNext();
    }
  };

  const renderImageUploadSection = (
    name: string,
    description?: string,
    maxImages: number = 4,
    titleSize: string = "base"
  ) => (
    <div
      key={name}
      className={`pb-1 rounded-md bg-backgroundAlt dark:bg-backgroundDarkAlt hover:bg-backgroundLight hover:dark:bg-backgroundLight ${
        errors[name] ? "border border-red-500" : "border-none"
      }`}
    >
      <div
        className="flex justify-between items-center cursor-pointer p-4"
        onClick={() => toggleSection(name)}
      >
        <h4 className={`text-${titleSize} font-semibold text-textPrimary`}>
          {name}
        </h4>
        <AiOutlineDown
          className={`${
            expandedSections[name] ? "rotate-180" : ""
          } transition-transform duration-300 text-textPrimary dark:text-textSecondary`}
        />
      </div>

      {description && (
        <p
          className="text-sm text-gray-600 dark:text-gray-400 -mt-3 mb-4 px-4 cursor-pointer"
          onClick={() => toggleSection(name)}
        >
          {description}
        </p>
      )}

      {expandedSections[name] && (
        <div className="px-4 pb-4">
          <input
            type="file"
            accept="image/*"
            multiple
            onChange={(e) => handleImageChange(name, e, maxImages)}
            className={`w-full px-3 py-2 rounded-lg bg-backgroundDark dark:bg-background text-textPrimary mt-2 ${
              errors[name] ? "border-red-500" : "border-none"
            }`}
          />
          <p className="mt-1 ml-2 text-sm text-gray-600 dark:text-gray-400">
            {images[name]?.length || 0} archivo(s) subido(s)
          </p>

          {errors[name] && (
            <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
              <AiOutlineExclamationCircle className="w-5 h-5" />
              {errors[name]}
            </div>
          )}
          <div className="flex flex-wrap mt-2">
            {images[name]?.map((file, index) => (
              <div
                key={index}
                className="relative m-1 w-20 h-20 bg-cover bg-center rounded-md"
                style={{ backgroundImage: `url(${URL.createObjectURL(file)})` }}
              >
                <button
                  type="button"
                  className="absolute top-0 right-0 bg-textPrimary dark:bg-textSecondary bg-opacity-50 text-white rounded-full p-1"
                  onClick={() => handleRemoveImage(name, index)}
                >
                  <AiOutlineClose className="w-3 h-3" />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );

  return (
    <div className="container mx-auto max-w-2xl p-6 bg-backgroundLight dark:bg-backgroundDark rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold text-primary dark:text-primaryLight text-center mb-6">
        Subir Imágenes de la Propiedad
      </h2>

      <form
        onSubmit={(e) => {
          e.preventDefault();
        }}
        className="space-y-6"
      >
        {imagesTypes.map((type) =>
          renderImageUploadSection(
            type.name,
            type.description,
            type.maxImagesAllowed,
            "lg"
          )
        )}

        {Array.isArray(formData.commonAreas) &&
          formData.commonAreas.length > 0 && (
            <div>
              <h3 className="text-lg font-semibold text-textPrimary dark:text-textPrimary">
                Imágenes por Áreas Comunes
              </h3>
              <p className="text-sm mb-3 text-gray-600 dark:text-gray-400">
                Sube imágenes relevantes de cada área común para proporcionar
                más información visual a los usuarios.
              </p>

              {formData.commonAreas?.map((area) =>
                renderImageUploadSection(area.name, undefined, 4, "base")
              )}
            </div>
          )}

        <StepNavigationButtons
          currentStep={currentStep}
          totalSteps={totalSteps}
          onPrevious={onPrevious}
          onNext={handleSubmit}
        />
      </form>
    </div>
  );
}

createProperty.locationPropertyForm.tsx:
"use client";

import { useState, useRef, useEffect } from "react";
import { LatLngTuple } from "leaflet";
import { AiOutlineExclamationCircle, AiOutlineClose } from "react-icons/ai";
import StepNavigationButtons from "../../stepNavigationButtons";
import LocationMap from "../locationMap";
import { useAddressSearch } from "@/app/hooks/useAddressSearch";
import { PropertyData } from "@/lib/definitios";

interface LocationPropertyFormProps {
  formData: PropertyData;
  onChange: (updatedData: Partial<PropertyData>) => void;
  onSubmit: (data: Partial<PropertyData>) => void;
  onPrevious: () => void;
  onNext: () => void;
  currentStep: number;
  totalSteps: number;
  locations: {
    departaments: { id: number; name: string }[];
    cities: Record<number, { id: number; name: string }[]>;
  };
}

export default function LocationPropertyForm({
  formData,
  onChange,
  onPrevious,
  onNext,
  currentStep,
  totalSteps,
  locations,
}: LocationPropertyFormProps) {
  const [errors, setErrors] = useState({
    departamentError: "",
    cityError: "",
    addressError: "",
  });

  const { results, loading, error, searchAddress } = useAddressSearch();
  const [isListVisible, setIsListVisible] = useState(false);
  const listRef = useRef<HTMLUListElement>(null);

  const handleAddressInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    onChange({ address: value });

    if (value.trim()) {
      searchAddress(value);
      setErrors((prevErrors) => ({ ...prevErrors, addressError: "" }));
      setIsListVisible(true);
    }
  };

  const handleDepartamentChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const departamentId = parseInt(e.target.value);
    const selectedDepartament = locations.departaments.find(
      (dep) => dep.id === departamentId
    );

    if (selectedDepartament) {
      onChange({
        city: {
          id: 0,
          name: "",
          departament: selectedDepartament,
        },
      });

      setErrors((prevErrors) => ({ ...prevErrors, departamentError: "" }));
    }
  };

  const handleCityChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const cityId = parseInt(e.target.value);
    const selectedCity = locations.cities[
      formData.city?.departament?.id || 0
    ]?.find((city) => city.id === cityId);

    if (selectedCity && formData.city?.departament) {
      onChange({
        city: {
          ...selectedCity,
          departament: formData.city.departament,
        },
      });

      setErrors((prevErrors) => ({ ...prevErrors, cityError: "" }));
    }
  };

  const handleAddressSelect = (
    lat: string,
    lon: string,
    displayName: string
  ) => {
    onChange({
      address: displayName,
      latitude: parseFloat(lat),
      longitude: parseFloat(lon),
    });

    setErrors((prevErrors) => ({ ...prevErrors, addressError: "" }));
  };

  const validateForm = () => {
    const newErrors = { ...errors };

    if (!formData.city?.departament?.id) {
      newErrors.departamentError = "El departamento es obligatorio.";
    }
    if (!formData.city?.id) {
      newErrors.cityError = "La ciudad es obligatoria.";
    }
    if (!formData.address?.trim()) {
      newErrors.addressError = "La dirección es obligatoria.";
    }

    setErrors(newErrors);
    return Object.values(newErrors).every((error) => error === "");
  };

  const handleNext = (e?: React.FormEvent) => {
    e?.preventDefault();
    if (validateForm()) {
      onNext();
    }
  };

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (listRef.current && !listRef.current.contains(event.target as Node)) {
        setIsListVisible(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="container mx-auto max-w-2xl p-6 bg-backgroundLight dark:bg-backgroundDark rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold text-primary dark:text-primaryLight text-center mb-6">
        Ubicación de la Propiedad
      </h2>

      <form className="space-y-6">
        {/* Departamento y Ciudad */}
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          <div>
            <label className="block text-textPrimary dark:text-textPrimary mb-2">
              Departamento <span className="text-red-500">*</span>
            </label>
            <select
              name="departament"
              value={formData.city?.departament?.id || ""}
              onChange={handleDepartamentChange}
              className={`w-full px-3 py-2 border rounded-md bg-background dark:bg-backgroundLight text-textPrimary dark:text-textPrimary ${
                errors.departamentError
                  ? "border-red-500"
                  : "border-borderColor"
              }`}
            >
              <option value="">Seleccione un departamento</option>
              {locations.departaments.map((dep) => (
                <option key={dep.id} value={dep.id}>
                  {dep.name}
                </option>
              ))}
            </select>
            {errors.departamentError && (
              <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                <AiOutlineExclamationCircle className="w-5 h-5" />
                {errors.departamentError}
              </div>
            )}
          </div>

          <div>
            <label className="block text-textPrimary dark:text-textPrimary mb-2">
              Ciudad <span className="text-red-500">*</span>
            </label>
            <select
              name="city"
              value={formData.city?.id || ""}
              onChange={handleCityChange}
              className={`w-full px-3 py-2 border rounded-md bg-background dark:bg-backgroundLight text-textPrimary dark:text-textPrimary ${
                errors.cityError ? "border-red-500" : "border-borderColor"
              }`}
            >
              <option value="">Seleccione una ciudad</option>
              {locations.cities[formData.city?.departament?.id || 0]?.map(
                (city) => (
                  <option key={city.id} value={city.id}>
                    {city.name}
                  </option>
                )
              )}
            </select>
            {errors.cityError && (
              <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                <AiOutlineExclamationCircle className="w-5 h-5" />
                {errors.cityError}
              </div>
            )}
          </div>
        </div>

        {/* Dirección */}
        <div className="relative">
          <label className="block text-textPrimary dark:text-textPrimary mb-2">
            Dirección <span className="text-red-500">*</span>
          </label>

          <div className="flex items-center">
            <input
              type="text"
              name="address"
              value={formData.address || ""}
              onChange={handleAddressInput}
              disabled={!formData.city || !formData.city.id}
              className={`w-full px-3 py-2 border rounded-md bg-background dark:bg-backgroundLight text-textPrimary dark:text-textPrimary ${
                errors.addressError ? "border-red-500" : "border-borderColor"
              }`}
              placeholder="Ingrese la dirección"
            />
            {formData.address && (
              <button
                type="button"
                className="absolute right-3 text-gray-500 hover:text-gray-700"
                onClick={() => onChange({ address: "" })}
              >
                <AiOutlineClose className="w-5 h-5" />
              </button>
            )}
          </div>

          <ul
            ref={listRef}
            className={`list-group mt-2 max-h-40 overflow-y-auto bg-background dark:bg-backgroundLight text-textPrimary dark:text-textPrimary border border-borderColor rounded-md shadow-lg ${
              isListVisible ? "block" : "hidden"
            }`}
          >
            {loading && <li className="p-2">Cargando...</li>}
            {error && <li className="p-2 text-red-500">{error}</li>}
            {results.map((result) => (
              <li
                key={`${result.lat}-${result.lon}`}
                className="list-group-item p-2 hover:bg-backgroundDark dark:hover:bg-backgroundDark cursor-pointer"
                onClick={() => {
                  handleAddressSelect(
                    result.lat,
                    result.lon,
                    result.display_name
                  );
                  setIsListVisible(false);
                }}
              >
                {result.display_name}
              </li>
            ))}
          </ul>

          {errors.addressError && (
            <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
              <AiOutlineExclamationCircle className="w-5 h-5" />
              {errors.addressError}
            </div>
          )}
        </div>

        {/* Mapa */}
        <LocationMap
          coordinates={
            [
              formData.latitude || 4.5709,
              formData.longitude || -74.2973,
            ] as LatLngTuple
          }
          onLocationSelect={(coords) =>
            onChange({ latitude: coords[0], longitude: coords[1] })
          }
        />

        <StepNavigationButtons
          currentStep={currentStep}
          totalSteps={totalSteps}
          onPrevious={onPrevious}
          onNext={handleNext}
        />
      </form>
    </div>
  );
}

createProperty.progressBar.tsx:
import {
  FaInfoCircle,
  FaMapMarkerAlt,
  FaBuilding,
  FaListAlt,
  FaImage,
} from "react-icons/fa";

type ProgressBarProps = {
  currentStep: number;
};

const steps = [
  { icon: FaInfoCircle },
  { icon: FaMapMarkerAlt },
  { icon: FaBuilding },
  { icon: FaListAlt },
  { icon: FaImage },
];

export default function ProgressBar({ currentStep }: ProgressBarProps) {
  return (
    <div className="flex justify-center items-center w-full max-w-xl mx-auto">
      {steps.map((step, index) => {
        const Icon = step.icon;
        const isLastStep = index === steps.length - 1;
        const isActive = currentStep >= index;

        return (
          <div
            key={index}
            className={`flex items-center ${isLastStep ? "" : "flex-1"}`}
          >
            {/* Icono dentro del círculo */}
            <div
              className={`w-12 h-12 z-10 rounded-full flex items-center justify-center transition-colors duration-300 ${
                isActive
                  ? "bg-primary text-white dark:bg-primaryLight"
                  : "bg-backgroundDark text-textSecondary dark:bg-backgroundLight dark:text-textPlaceholder"
              }`}
            >
              <Icon size={25} />
            </div>

            {/* Línea de separación, solo si no es el último paso */}
            {!isLastStep && (
              <div
                className={`h-1 transition-colors duration-300 ${
                  currentStep > index
                    ? "bg-borderColor dark:bg-borderColorHover"
                    : "bg-backgroundDark dark:bg-backgroundLight"
                }`}
                style={{
                  width: "calc(100% - 2.25rem)",
                  marginLeft: "-4px",
                }}
              ></div>
            )}
          </div>
        );
      })}
    </div>
  );
}

filter.filterMapControls.tsx:
"use client";

import { useState, useEffect } from "react";
import { FaFilter, FaChevronDown } from "react-icons/fa";
import { MdMap } from "react-icons/md";
import { BiSolidBuildingHouse } from "react-icons/bi";

type FilterMapControlsProps = {
  filterOpen: boolean;
  setFilterOpen: React.Dispatch<React.SetStateAction<boolean>>;
  showMap: boolean;
  setShowMap: React.Dispatch<React.SetStateAction<boolean>>;
};

export default function FilterMapControls({
  filterOpen,
  setFilterOpen,
  showMap,
  setShowMap,
}: FilterMapControlsProps) {
  const [showMapButton, setShowMapButton] = useState(false);
  const [isFilterVisible, setIsFilterVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      let scrollThreshold = 550;

      if (window.matchMedia("(min-width: 640px)").matches) {
        scrollThreshold = 400;
      }
      if (window.matchMedia("(min-width: 768px)").matches) {
        scrollThreshold = 300;
      }

      if (showMap) {
        setShowMapButton(true);
        setFilterOpen(false);
      } else {
        if (window.scrollY > scrollThreshold) {
          setShowMapButton(true);
        } else {
          setShowMapButton(false);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [showMap]);

  const handleToggleMap = () => {
    setShowMap((prev) => !prev);
    setIsFilterVisible(false);
  };

  const toggleFilterVisibility = () => {
    setIsFilterVisible((prev) => !prev);
    setFilterOpen((prev) => !prev);
  };

  return (
    <>
      <div
        className={`flex items-center px-4 sm:px-20 gap-4 ${
          showMap ? "justify-end my-4 hidden" : "justify-between mt-4"
        }`}
      >
        <button
          onClick={toggleFilterVisibility}
          className={`flex items-center space-x-2 px-6 py-2 bg-transparent border border-borderColor dark:border-borderColorHover rounded-full hover:bg-backgroundLight dark:hover:bg-backgroundDark transition-colors whitespace-nowrap ${
            showMap ? "hidden" : "block"
          }`}
        >
          <span className="text-textPrimary dark:text-textPrimary">Filtro</span>
          <FaFilter className="text-textPrimary dark:text-textPrimary" />
          <FaChevronDown
            className={`${
              isFilterVisible ? "rotate-180" : ""
            } transition-transform duration-300 text-textPrimary dark:text-textPrimary`}
          />
        </button>

        <button
          className="flex items-center space-x-2 px-6 py-2 rounded-full bg-transparent border border-borderColor dark:border-borderColorHover hover:bg-backgroundLight dark:hover:bg-backgroundDark transition-colors whitespace-nowrap"
          onClick={handleToggleMap}
        >
          <span className="text-textPrimary dark:text-textPrimary">
            {showMap ? "Propiedades" : "Mapa"}
          </span>
          {showMap ? (
            <BiSolidBuildingHouse
              size={18}
              className="text-textPrimary dark:text-textPrimary"
            />
          ) : (
            <MdMap
              size={18}
              className="text-textPrimary dark:text-textPrimary"
            />
          )}
        </button>
      </div>

      {showMapButton && (
        <button
          className={`fixed px-4 right-6 z-50 py-3 mt-24 text-sm rounded-full bg-background dark:bg-backgroundDark shadow-lg hover:bg-backgroundLight dark:hover:bg-backgroundDark transition-colors border border-borderColor dark:border-borderColorHover flex items-center space-x-2 ${
            showMap ? "bottom-4 lg:top-0 lg:bottom-auto" : "top-6"
          }`}
          onClick={handleToggleMap}
        >
          <span className="text-textPrimary dark:text-textPrimary">
            {showMap ? "Propiedades" : "Mapa"}
          </span>
          {showMap ? (
            <BiSolidBuildingHouse
              size={18}
              className="text-textPrimary dark:text-textPrimary"
            />
          ) : (
            <MdMap
              size={18}
              className="text-textPrimary dark:text-textPrimary"
            />
          )}
        </button>
      )}
    </>
  );
}

