filter.filterOptionWithSlider.tsx:
import { useState, useEffect } from "react";
import { FaChevronDown } from "react-icons/fa";

type FilterOptionWithSliderProps = {
  label: string;
  icon: React.ReactNode;
  value: number;
  min: number;
  max: number;
  step: number;
  onChange: (value: number) => void;
  isOpen: boolean;
  setIsOpen: (isOpen: boolean) => void;
  category: string;
  prefixText: string;
  suffixText: string;
};

export default function FilterOptionWithSlider({
  label,
  icon,
  value,
  min,
  max,
  step,
  onChange,
  isOpen,
  setIsOpen,
  category,
  prefixText,
  suffixText,
}: FilterOptionWithSliderProps) {
  const [sliderValue, setSliderValue] = useState(value);

  const handleSliderChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = parseInt(event.target.value, 10);
    setSliderValue(newValue);
  };

  const handleSliderComplete = () => {
    onChange(sliderValue);
  };

  useEffect(() => {
    setSliderValue(value);
  }, [value]);

  const formatDisplayValue = (value: number) => {
    if (value >= 1000000000) {
      const newValue = value / 1000000000;
      return `${newValue.toFixed(3)}M`;
    } else if (value >= 1000000) {
      const newValue = value / 1000000;
      return Number.isInteger(newValue)
        ? `${newValue}M`
        : `${newValue.toFixed(1)}M`;
    } else if (value >= 100000) {
      const newValue = value / 1000;
      return Number.isInteger(newValue)
        ? `${newValue}K`
        : `${newValue.toFixed(1)}K`;
    } else if (value >= 1000) {
      const newValue = value / 1000;
      return newValue.toFixed(3);
    } else {
      return value.toString();
    }
  };

  return (
    <div>
      <div
        className="flex items-center justify-between cursor-pointer mb-4 transition-transform duration-200 transform hover:scale-105 hover:font-semibold text-textPrimary dark:text-textPrimary"
        onClick={() => setIsOpen(!isOpen)}
      >
        <div
          className={`flex items-center space-x-2 text-lg font-medium ${
            sliderValue > 1
              ? "text-primary dark:text-primaryLight"
              : "text-textPrimary dark:text-textPrimary"
          }`}
        >
          {icon}
          <span>{label}</span>
        </div>
        <FaChevronDown
          className={`transition-transform duration-300 transform ${
            isOpen ? "rotate-180" : ""
          } text-textPrimary dark:text-textPrimary`}
        />
      </div>
      {isOpen && (
        <div className="flex flex-col items-center space-y-2 mb-6 px-4">
          <input
            type="range"
            min={min}
            max={max}
            step={sliderValue > 10000000 ? 10000000 : step}
            value={sliderValue}
            onChange={handleSliderChange}
            onMouseUp={handleSliderComplete}
            onTouchEnd={handleSliderComplete}
            className="w-full h-1 bg-primary rounded-full appearance-none cursor-pointer dark:bg-primaryLight"
          />
          <style jsx>{`
            input[type="range"]::-webkit-slider-thumb {
              appearance: none;
              height: 20px;
              width: 20px;
              background-color: var(--primary);
              border-radius: 50%;
              cursor: pointer;
              border: 2px solid var(--backgroundAlt);
            }

            input[type="range"]::-moz-range-thumb {
              height: 20px;
              width: 20px;
              background-color: var(--primary);
              border-radius: 50%;
              cursor: pointer;
              border: 2px solid var(--backgroundAlt);
            }

            input[type="range"]::-ms-thumb {
              height: 20px;
              width: 20px;
              background-color: var(--primary);
              border-radius: 50%;
              cursor: pointer;
              border: 2px solid var(--backgroundAlt);
            }
          `}</style>
          {sliderValue > 1 && (
            <div className="text-center text-base text-textPrimary dark:text-textPrimary">
              <span>{prefixText} </span>
              <span className="font-bold text-2xl">
                {category === "price" ? "$" : "+"}
                {formatDisplayValue(sliderValue)}
              </span>
              <span> {suffixText}</span>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

filter.propertyFilter.tsx:
"use client";

import { useState, useEffect, useRef } from "react";
import axios from "axios";
import FilterOption from "./filterOption";
import FilterNumericCounter from "./filterNumericCounter";
import FilterOptionWithSlider from "./filterOptionWithSlider";
import {
  FaCity,
  FaBuilding,
  FaHome,
  FaDollarSign,
  FaBed,
  FaToilet,
  FaCouch,
  FaWarehouse,
  FaConciergeBell,
  FaTimes,
  FaCrown,
  FaRulerHorizontal,
} from "react-icons/fa";
import {
  City,
  HousingType,
  PropertyType,
  NearbyService,
  CommonArea,
  MembershipSummary,
} from "@/lib/definitios";

type Category =
  | "cities"
  | "propertyTypes"
  | "housingTypes"
  | "commonAreas"
  | "nearbyServices"
  | "memberships"
  | "rooms"
  | "bathrooms"
  | "lobbies"
  | "price"
  | "area";

type PropertyFilterProps = {
  selectedButtons: Record<Category, number[]>;
  setSelectedButtons: React.Dispatch<
    React.SetStateAction<Record<Category, number[]>>
  >;
  setFilterOpen: React.Dispatch<React.SetStateAction<boolean>>;
  priceRange: { min: number; max: number };
  areaRange: { min: number; max: number };
};

export default function PropertyFilter({
  selectedButtons,
  setSelectedButtons,
  setFilterOpen,
  priceRange,
  areaRange,
}: PropertyFilterProps) {
  const [metadata, setMetadata] = useState({
    cities: [] as City[],
    commonAreas: [] as CommonArea[],
    housingTypes: [] as HousingType[],
    nearbyServices: [] as NearbyService[],
    propertyTypes: [] as PropertyType[],
    memberships: [] as MembershipSummary[],
  });

  const [isOpen, setIsOpen] = useState<Record<Category, boolean>>({
    cities: false,
    propertyTypes: false,
    housingTypes: false,
    commonAreas: false,
    nearbyServices: false,
    memberships: false,
    rooms: false,
    bathrooms: false,
    lobbies: false,
    price: false,
    area: false,
  });

  const [isFixed, setIsFixed] = useState(false);
  const filterRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("/api/properties/metadata");
        setMetadata(response.data);
      } catch (error) {
        console.error("Error fetching metadata:", error);
      }
    };
    fetchData();
  }, []);

  const toggleOpen = (category: Category) => {
    setIsOpen((prevState) => ({
      ...prevState,
      [category]: !prevState[category],
    }));
  };

  const [bottomReached, setBottomReached] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const filterTop = filterRef.current?.getBoundingClientRect().top ?? 0;
      const filterBottom =
        filterRef.current?.getBoundingClientRect().bottom ?? 0;
      const scrollY = window.scrollY;
      const bottomOffset = 30;

      if (scrollY >= filterTop + bottomOffset && !bottomReached) {
        setIsFixed(true);
      } else {
        setIsFixed(false);
      }

      const pageHeight = document.documentElement.scrollHeight;
      const windowHeight = window.innerHeight;
      if (windowHeight + scrollY >= pageHeight - 200) {
        setBottomReached(true);
        setIsFixed(false);
      } else {
        setBottomReached(false);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [bottomReached]);

  const updateFilterValue = (
    category: Category,
    newValue: number | number[]
  ) => {
    setSelectedButtons((prevSelected) => ({
      ...prevSelected,
      [category]: Array.isArray(newValue) ? newValue : [newValue],
    }));
  };

  const changeCount = (
    category: "rooms" | "bathrooms" | "lobbies",
    increment: boolean
  ) => {
    setSelectedButtons((prevSelected) => ({
      ...prevSelected,
      [category]: [
        Math.max(
          1,
          Math.min(prevSelected[category][0] + (increment ? 1 : -1), 3)
        ),
      ],
    }));
  };

  const handleToggleItem = (category: Category, id: number) => {
    setSelectedButtons((prevSelected) => {
      const selectedItems = prevSelected[category];
      return {
        ...prevSelected,
        [category]: selectedItems.includes(id)
          ? selectedItems.filter((itemId) => itemId !== id)
          : [...selectedItems, id],
      };
    });
  };

  const resetFilters = () => {
    setSelectedButtons({
      cities: [],
      propertyTypes: [],
      housingTypes: [],
      commonAreas: [],
      nearbyServices: [],
      memberships: [],
      rooms: [1],
      bathrooms: [1],
      lobbies: [1],
      price: [1],
      area: [1],
    });
  };

  return (
    <div
      ref={filterRef}
      className={`w-full sm:w-72 md:w-80 bg-background dark:bg-backgroundDark shadow-md rounded-lg py-4 transition-all duration-300 ease-in-out ${
        isFixed ? "sticky top-32 lg:top-20 z-10" : ""
      }`}
    >
      <div className="mt-1 space-y-6">
        <div className="flex items-center justify-between rounded-md pl-2 pr-6">
          <button
            onClick={resetFilters}
            className="font-medium text-lg py-1 px-4 text-textPrimary dark:text-textPrimary transition-transform duration-200 transform hover:scale-110 hover:font-semibold"
          >
            Borrar filtros
          </button>
          <div
            className="flex items-center cursor-pointer text-textPrimary dark:text-textPrimary"
            onClick={() => setFilterOpen((prev) => !prev)}
          >
            <FaTimes className="transition-transform duration-200 transform hover:scale-125" />
          </div>
        </div>

        <hr className="border-borderColor dark:border-borderColorHover" />

        <div className="space-y-4 px-3 py-1 sm:max-h-[65vh] sm:overflow-y-auto overflow-x-hidden">
          {[
            {
              label: "Ciudades",
              icon: <FaCity />,
              items: metadata.cities,
              category: "cities" as Category,
            },
            {
              label: "Tipos de Inmueble",
              icon: <FaBuilding />,
              items: metadata.propertyTypes,
              category: "propertyTypes" as Category,
            },
            {
              label: "Tipos de Vivienda",
              icon: <FaHome />,
              items: metadata.housingTypes,
              category: "housingTypes" as Category,
            },
            {
              label: "Áreas Comunes",
              icon: <FaWarehouse />,
              items: metadata.commonAreas,
              category: "commonAreas" as Category,
            },
            {
              label: "Servicios Cercanos",
              icon: <FaConciergeBell />,
              items: metadata.nearbyServices,
              category: "nearbyServices" as Category,
            },
            {
              label: "Membresía",
              icon: <FaCrown />,
              items: metadata.memberships,
              category: "memberships" as Category,
            },
          ].map(({ label, icon, items, category }) => (
            <FilterOption
              key={category}
              label={label}
              icon={icon}
              items={items}
              selectedItems={selectedButtons[category]}
              onToggleItem={(id) => handleToggleItem(category, id)}
              isOpen={isOpen[category]}
              onToggleOpen={() => toggleOpen(category)}
            />
          ))}

          {[
            {
              label: "Precio",
              icon: <FaDollarSign />,
              value: selectedButtons.price[0] || 0,
              min: priceRange.min || 0,
              max: priceRange.max || 0,
              step: 100000,
              category: "price" as Category,
              prefixText: "Desde",
              suffixText: "COP",
            },
            {
              label: "Área",
              icon: <FaRulerHorizontal />,
              value: selectedButtons.area[0] || 0,
              min: areaRange.min || 0,
              max: areaRange.max || 0,
              step: 100,
              category: "area" as Category,
              prefixText: "Desde",
              suffixText: "m²",
            },
          ].map(
            ({
              label,
              icon,
              value,
              min,
              max,
              step,
              category,
              prefixText,
              suffixText,
            }) => (
              <FilterOptionWithSlider
                key={category}
                label={label}
                icon={icon}
                value={value}
                min={min}
                max={max}
                step={step}
                onChange={(newValue) => updateFilterValue(category, newValue)}
                isOpen={isOpen[category]}
                setIsOpen={() => toggleOpen(category)}
                category={category}
                prefixText={prefixText}
                suffixText={suffixText}
              />
            )
          )}

          {[
            {
              label: "Nº de habitaciones",
              icon: <FaBed />,
              category: "rooms" as "rooms" | "bathrooms" | "lobbies",
            },
            {
              label: "Nº de baños",
              icon: <FaToilet />,
              category: "bathrooms" as "rooms" | "bathrooms" | "lobbies",
            },
            {
              label: "Nº de salas de estar",
              icon: <FaCouch />,
              category: "lobbies" as "rooms" | "bathrooms" | "lobbies",
            },
          ].map(({ label, icon, category }) => (
            <FilterNumericCounter
              key={category}
              label={label}
              icon={icon}
              isOpen={isOpen[category]}
              setIsOpen={() => toggleOpen(category)}
              value={selectedButtons[category][0]}
              increment={() => changeCount(category, true)}
              decrement={() => changeCount(category, false)}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

properties.locationMap.tsx:
"use client";

import { useEffect } from "react";
import { LatLngTuple, Map as LeafletMap } from "leaflet";
import { useMap, MapContainer, TileLayer, Marker } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import markerIconPng from "leaflet/dist/images/marker-icon.png";
import markerShadowPng from "leaflet/dist/images/marker-shadow.png";

const markerIcon = new L.Icon({
  iconUrl: markerIconPng.src,
  shadowUrl: markerShadowPng.src,
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41],
});

function CenterMap({ coordinates }: { coordinates: LatLngTuple }) {
  const map = useMap();

  useEffect(() => {
    map.setView(coordinates, 13);
  }, [coordinates, map]);

  return null;
}

function LocationMarker({
  onLocationSelect,
}: {
  onLocationSelect: (latlng: LatLngTuple) => void;
}) {
  const map = useMap();

  useEffect(() => {
    const handleClick = (event: L.LeafletMouseEvent) => {
      const latlng: LatLngTuple = [event.latlng.lat, event.latlng.lng];
      onLocationSelect(latlng);
    };

    map.on("click", handleClick);
    return () => {
      map.off("click", handleClick);
    };
  }, [map, onLocationSelect]);

  return null;
}

export default function LocationMap({
  coordinates,
  onLocationSelect,
}: {
  coordinates: LatLngTuple;
  onLocationSelect: (coordinates: LatLngTuple) => void;
}) {
  return (
    <div className="w-full h-64">
      <MapContainer
        center={coordinates}
        zoom={13}
        className="w-full h-full z-10"
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        <Marker position={coordinates} icon={markerIcon} />
        <LocationMarker onLocationSelect={onLocationSelect} />
        <CenterMap coordinates={coordinates} />
      </MapContainer>
    </div>
  );
}

