                className="px-4 py-2 bg-textPlaceholder hover:bg-borderColor dark:bg-textPrimary dark:hover:bg-textSecondary text-white rounded-lg mr-2 hover:scale-105 transition transform duration-300"
              >
                Cancelar
              </button>
              <button
                type="submit"
                className="px-4 py-2 bg-primary hover:bg-primaryDark dark:bg-primaryLight dark:hover:bg-primaryDark text-white rounded-lg hover:scale-105 transition transform duration-300"
              >
                Confirmar
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default MembershipModal;

properties.PriceMarker.tsx:
"use client";

import { useEffect } from "react";
import L from "leaflet";
import { useMap } from "react-leaflet";

type PriceMarkerProps = {
  position: [number, number];
  price: number;
  onClick: () => void;
  isSelected: boolean;
};

export default function PriceMarker({
  position,
  price,
  onClick,
  isSelected,
}: PriceMarkerProps) {
  const map = useMap();

  useEffect(() => {
    const marker = L.marker(position, {
      icon: L.divIcon({
        html: `<button class="text-white font-semibold py-2 px-3 rounded-full shadow-md ${
          isSelected
            ? "bg-primaryDark dark:bg-primaryDark"
            : "bg-primary dark:bg-primary hover:bg-primaryLight focus:outline-none focus:bg-primaryDark dark:focus:bg-primaryDark"
        }">$${price.toLocaleString()}</button>`,
        iconAnchor: [12, 41],
        popupAnchor: [0, -41],
        className: "",
      }),
    }).addTo(map);

    marker.on("click", onClick);

    return () => {
      map.removeLayer(marker);
    };
  }, [map, position, price, onClick, isSelected]); // isSelected agregado como dependencia

  return null;
}

