import { useState, useEffect } from "react";
import { FaTimes, FaEdit, FaTrash } from "react-icons/fa";
import Link from "next/link";

type PropertyCardProps = {
  images: string[];
  name: string;
  location: string;
  price: number;
  area: number;
  idMembership: number;
  isFromMap: boolean;
  showActions: boolean;
  onClose?: (() => void) | null;
  url: string;
};

export default function PropertyCard({
  images,
  name,
  location,
  price,
  area,
  idMembership,
  isFromMap,
  showActions,
  onClose,
  url,
}: PropertyCardProps) {
  const [currentImage, setCurrentImage] = useState(0);
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isHovered) {
      interval = setInterval(
        () => setCurrentImage((prev) => (prev + 1) % images.length),
        1000
      );
    }
    return () => clearInterval(interval);
  }, [isHovered, images.length]);

  return (
    <div className="bg-background dark:bg-backgroundDark rounded-lg">
      <Link href={url} target="_blank" rel="noopener noreferrer">
        <div
          className={`relative max-w-xs shadow-secondary dark:shadow-white rounded-lg overflow-hidden bg-secondary dark:bg-secondaryDark cursor-pointer p-1 shadow-md dark:shadow-md ${
            isFromMap
              ? "z-50 hover:scale-105 transition-transform duration-300"
              : "h-[370px] z-0"
          } ${
            idMembership === 1003
              ? "bg-gradient-to-r from-primaryLight to-primaryDark hover:from-primary hover:to-primaryDark"
              : ""
          }`}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => {
            setIsHovered(false);
            setCurrentImage(0);
          }}
        >
          {showActions && (
            <div className="absolute top-3 right-3 flex space-x-2 z-50">
              <button className="p-2 rounded-full bg-primaryLight text-white hover:bg-primaryDark transition-colors">
                <FaEdit />
              </button>
              <button className="p-2 rounded-full bg-red-500 text-white hover:bg-red-600 transition-colors">
                <FaTrash />
              </button>
            </div>
          )}

          <div className="bg-background dark:bg-secondaryDark rounded-lg h-full flex flex-col relative">
            {onClose && (
              <button
                onClick={(e) => {
                  e.preventDefault();
                  onClose();
                }}
                className="absolute top-2 right-2 z-20 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-75 transition-all"
              >
                <FaTimes />
              </button>
            )}

            <div
              className={`relative w-full z-0 overflow-hidden ${
                !isFromMap ? "flex-shrink-0 h-full" : "h-72"
              }`}
            >
              {images.map((img, index) => (
                <img
                  key={index}
                  src={img}
                  alt="Property"
                  className={`w-full h-full object-cover absolute top-0 left-0 transition-opacity duration-1000 ease-in-out ${
                    index === currentImage ? "opacity-100" : "opacity-0"
                  }`}
                />
              ))}
            </div>

            {/* Precio fijo en la esquina superior izquierda */}
            <div className="absolute top-2 left-2 bg-black bg-opacity-75 text-white text-sm px-2 py-1 rounded-full z-50">
              ${price.toLocaleString()} COP
            </div>

            <div className="px-2 pt-16 pb-2 z-20 absolute bottom-0 left-0 right-0 bg-gradient-to-t from-secondaryDark dark:from-black via-secondaryDark dark:via-gray-800 to-transparent">
              <h3 className="text-lg font-semibold text-white leading-tight break-words line-clamp-1">
                {name}
              </h3>
              <p className="text-sm text-gray-300">{location}</p>
              {area > 0 && (
                <p className="text-xs text-gray-300">Área: {area} m²</p>
              )}
            </div>
          </div>
        </div>
      </Link>
    </div>
  );
}

properties.table.tsx:
import { useState } from "react";
import {
  FaTrash,
  FaEdit,
  FaChevronLeft,
  FaChevronRight,
  FaSortUp,
  FaSortDown,
} from "react-icons/fa";

const mergeSort = <T,>(
  array: T[],
  key: keyof T,
  direction: "asc" | "desc"
): T[] => {
  if (array.length <= 1) return array;

  const middle = Math.floor(array.length / 2);
  const left = mergeSort(array.slice(0, middle), key, direction);
  const right = mergeSort(array.slice(middle), key, direction);

  return merge(left, right, key, direction);
};

const merge = <T,>(
  left: T[],
  right: T[],
  key: keyof T,
  direction: "asc" | "desc"
): T[] => {
  const result: T[] = [];
  let indexLeft = 0;
  let indexRight = 0;

  while (indexLeft < left.length && indexRight < right.length) {
    const leftValue = left[indexLeft][key];
    const rightValue = right[indexRight][key];

    const comparison =
      typeof leftValue === "string"
        ? leftValue.localeCompare(rightValue as string)
        : (leftValue as number) - (rightValue as number);

    if (
      (direction === "asc" && comparison <= 0) ||
      (direction === "desc" && comparison > 0)
    ) {
      result.push(left[indexLeft++]);
    } else {
      result.push(right[indexRight++]);
    }
  }

  return result.concat(left.slice(indexLeft), right.slice(indexRight));
};

type TableProps<T extends Record<string, any>> = {
  data: T[];
  headers: { label: string; key: keyof T }[];
  totalEntries: number;
  entry: string;
  stateLabels?: { true: string; false: string };
  currentPage: number;
  totalPages: number;
  goToPage: (pageNumber: number) => void;
  goToNextPage: () => void;
  goToPreviousPage: () => void;
  entriesPerPage: number;
  handleEntriesPerPageChange: (e: React.ChangeEvent<HTMLSelectElement>) => void;
  handleSearchChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  canDelete: boolean;
  onEditClick: (item: T) => void;
};

export default function Table<
  T extends { id: number; price?: number; state?: boolean }
>({
  data,
  headers,
  totalEntries,
  entry,
  stateLabels = { true: "Activo", false: "Inactivo" },
  currentPage,
  totalPages,
  goToPage,
  goToNextPage,
  goToPreviousPage,
  entriesPerPage,
  handleEntriesPerPageChange,
  handleSearchChange,
  canDelete,
  onEditClick,
}: TableProps<T>) {
  const [sortColumn, setSortColumn] = useState<keyof T | null>(null);
  const [sortDirection, setSortDirection] = useState<"asc" | "desc" | null>(
    null
  );

  const handleSort = (columnKey: keyof T) => {
    setSortColumn(columnKey);
    setSortDirection((prevDirection) =>
      prevDirection === "asc" ? "desc" : "asc"
    );
  };

  const sortedData = sortColumn
    ? mergeSort(data, sortColumn, sortDirection || "asc")
    : data;

  return (
    <>
      {/* Opciones de mostrar por página y campo de búsqueda */}
      <div className="flex flex-col sm:flex-row sm:justify-between gap-3 sm:gap-0 sm:items-center mb-4 md:px-3 text-xs sm:text-sm">
        <div className="flex items-center space-x-2 px-2 sm:px-0">
          <span className="text-textPrimary dark:text-textPrimary">
            Mostrar
          </span>
          <select
            value={entriesPerPage}
            onChange={handleEntriesPerPageChange}
            className="border border-borderColor dark:border-borderColorHover focus:border-borderColorHover dark:focus:border-borderColorHover rounded-md px-2 py-1 focus:outline-none bg-background dark:bg-backgroundDark text-textPrimary dark:text-textPrimary"
          >
            {[5, 10, 15, 20].map((num) => (
              <option key={num} value={num}>
                {num}
              </option>
            ))}
          </select>
          <span className="text-textPrimary dark:text-textPrimary">
            {entry} por página
          </span>
        </div>
        <div className="relative">
          <input
            type="text"
            onChange={handleSearchChange}
            placeholder="Buscar..."
            className="border border-borderColor dark:border-borderColorHover focus:border-borderColorHover dark:focus:border-borderColorHover rounded-md px-3 py-2 w-full sm:w-auto focus:outline-none bg-background dark:bg-backgroundDark text-textPrimary dark:text-textPrimary"
          />
        </div>
      </div>

      {/* Tabla dinámica */}
      <div className="table-responsive overflow-x-auto">
        <table className="table table-flush text-sm">
          <thead className="thead-light text-xs">
            <tr>
              {headers.map(({ label, key }) => (
                <th
                  key={key as string}
                  className="py-2 px-4 text-left cursor-pointer text-textPrimary dark:text-textPrimary"
                  onClick={() => handleSort(key)}
                >
                  <div className="flex items-center">
                    {label.toUpperCase()}
                    <div className="ml-2 flex flex-col gap-0">
                      <FaSortUp
                        className={`hover:text-primary dark:hover:text-primaryDark ${
                          sortColumn === key && sortDirection === "asc"
                            ? "text-primary dark:text-primary"
                            : "text-textPlaceholder dark:text-textSecondary"
                        }`}
                      />
                      <FaSortDown
                        className={`hover:text-primary dark:hover:text-primaryDark ${
                          sortColumn === key && sortDirection === "desc"
                            ? "text-primary dark:text-primary"
                            : "text-textPlaceholder dark:text-textSecondary"
                        }`}
                      />
                    </div>
                  </div>
                </th>
              ))}
              <th className="py-2 px-4 text-left text-textPrimary dark:text-textPrimary">
                ACCIÓN
              </th>
            </tr>
          </thead>
          <tbody>
            {sortedData.map((item) => (
              <tr
                key={item.id}
                className="border-t border-borderColor dark:border-borderColorHover max-h-28 hover:bg-backgroundLight dark:hover:bg-backgroundDark"
              >
                {headers.map(({ key }, index) => (
                  <td
                    key={index}
                    className="py-2 px-4 text-textPrimary dark:text-textPrimary"
                  >
                    {typeof item[key] === "number" ? (
                      item[key]?.toLocaleString()
                    ) : typeof item[key] === "boolean" ? (
                      <span
                        className={`py-1 px-3 rounded-full text-white ${
                          item[key] ? "bg-green-500" : "bg-red-500"
                        }`}
                      >
                        {item[key] ? stateLabels.true : stateLabels.false}
                      </span>
                    ) : (
                      String(item[key] ?? "")
                    )}
                  </td>
                ))}
                <td className="py-2 px-4 flex justify-center items-center space-x-2">
                  <button
                    onClick={() => onEditClick(item)}
                    className="text-primary dark:text-primaryLight hover:text-primaryDark dark:hover:text-primaryDark"
                  >
                    <FaEdit className="w-4 h-4" />
                  </button>
                  {canDelete && (
                    <button className="text-red-500 hover:text-red-700">
                      <FaTrash className="w-4 h-4" />
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Paginación */}
      <div className="flex justify-between items-center text-sm mt-4 md:px-3 text-textPrimary dark:text-textPrimary">
        <span>
          Mostrando {currentPage * entriesPerPage - entriesPerPage + 1} a{" "}
          {Math.min(currentPage * entriesPerPage, totalEntries)} de{" "}
          {totalEntries} {entry}
        </span>

        <div className="flex space-x-2 items-center">
          <button
            onClick={goToPreviousPage}
            className={`${
              currentPage === 1
                ? "text-textPlaceholder"
                : "text-primary dark:text-primaryLight"
            } p-2 rounded-full border border-borderColor dark:border-borderColorHover focus:outline-none`}
            disabled={currentPage === 1}
          >
            <FaChevronLeft />
          </button>

          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i + 1}
              onClick={() => goToPage(i + 1)}
              className={`px-3 py-1 rounded-full transition-colors duration-300 ${
                currentPage === i + 1
                  ? "bg-primaryLight dark:bg-primary hover:bg-primary dark:hover:bg-primaryDark text-white"
                  : "bg-backgroundLight dark:bg-backgroundDark hover:bg-primaryLight dark:hover:bg-primary text-textPrimary dark:text-textPrimary"
              }`}
            >
              {i + 1}
            </button>
          ))}

          <button
            onClick={goToNextPage}
            className={`${
              currentPage === totalPages
                ? "text-textPlaceholder"
                : "text-primary dark:text-primaryLight"
            } p-2 rounded-full border border-borderColor dark:border-borderColorHover focus:outline-none`}
            disabled={currentPage === totalPages}
          >
            <FaChevronRight />
          </button>
        </div>
      </div>
    </>
  );
}

ui.stepNavigationButtons.tsx:
"use client";

interface StepNavigationButtonsProps {
  currentStep: number;
  totalSteps: number;
  onPrevious: (e?: React.FormEvent) => void;
  onNext: (e?: React.FormEvent) => void;
}

export default function StepNavigationButtons({
  currentStep,
  totalSteps,
  onPrevious,
  onNext,
}: StepNavigationButtonsProps) {
  return (
    <div className="mt-6 flex justify-between">
      {currentStep > 0 && (
        <button
          className="bg-secondary text-sm text-white px-4 py-2 rounded-md hover:bg-secondaryLight dark:bg-secondary dark:hover:bg-secondaryLight"
          onClick={(e) => onPrevious(e)}
        >
          Anterior
        </button>
      )}
      <div className="flex-grow"></div>
      {currentStep < totalSteps && (
        <button
          className="bg-primary text-sm text-white px-4 py-2 rounded-md hover:bg-primaryLight dark:bg-primaryDark dark:hover:bg-primaryLight"
          onClick={(e) => onNext(e)}
        >
          {currentStep != totalSteps - 1 ? "Siguiente" : "Confirmar"}
        </button>
      )}
    </div>
  );
}

