      ) : (
        <ProjectsContainer
          projects={projects}
          totalEntries={totalEntries}
          isLoading={isLoading}
          fetchMoreProjects={fetchMoreProjects}
          filterOpen={filterOpen}
          showMap={showMap}
          setFilterOpen={setFilterOpen}
          priceRange={priceRange}
          areaRange={areaRange}
          selectedButtons={selectedButtons}
          setSelectedButtons={setSelectedButtons}
          isProperty={false}
        />
      )}
    </div>
  );
}

