import NextAuth from "next-auth";
import GoogleProvider from "next-auth/providers/google";
import { db } from "@/lib/db";
import { RowDataPacket } from "mysql2";

const handler = NextAuth({
  providers: [
    GoogleProvider({
      clientId: process.env.GOOGLE_CLIENT_ID || "",
      clientSecret: process.env.GOOGLE_CLIENT_SECRET || "",
    }),
  ],
  secret: process.env.AUTH_SECRET,
  pages: {
    signIn: "/login",
  },
  callbacks: {
    async jwt({ token, user, account }) {
      if (account?.provider === "google" && user) {
        const { email, name, image } = user;
        const givenName = name?.split(" ")[0] || "";
        const familyName = name?.split(" ").slice(1).join(" ") || "";

        const [result] = await db.query("CALL get_user_session(?)", [email]);
        let rows = (result as RowDataPacket[][])[0];

        if (rows.length === 0) {
          await db.query("CALL insert_user_provider(?, ?, ?, ?, ?, ?)", [
            name,
            givenName,
            familyName,
            email,
            image,
            "google",
          ]);

          const [updatedResult] = await db.query("CALL get_user_session(?)", [
            email,
          ]);
          rows = (updatedResult as RowDataPacket[][])[0];
        }

        await db.query("CALL update_user_last_login(?)", [rows[0].id]);

        token.id = rows[0].id;
        token.role = rows[0].role;
      }
      return token;
    },

    async session({ session, token }) {
      session.user.id = token.id + "";
      session.user.role = token.role + "";
      return session;
    },
  },
  debug: process.env.NODE_ENV === "development",
});

export { handler as GET, handler as POST };

login.route.ts:
import { NextResponse } from "next/server";
import { db } from "@/lib/db";
import bcrypt from "bcrypt";
import { RowDataPacket } from "mysql2";
import { User } from "@/lib/definitios";

export async function POST(request: Request) {
  const { email, password } = await request.json();

  const [result] = await db.query("CALL get_user(?)", [email]);

  const row = (result as RowDataPacket[][])[0][0];

  if (!row) {
    return NextResponse.json({ error: "User not found" }, { status: 404 });
  }

  const user: User = {
    id: row.id,
    names: row.names,
    lastnames: row.lastnames,
    email: row.email,
    password: row.password,
    role: row.roleId,
    membershipId: row.membershipId,
  };

  const passwordsMatch = await bcrypt.compare(password, user.password);

  if (passwordsMatch) {
    return NextResponse.json({
      id: user.id,
      names: user.names,
      email: user.email,
      role: user.role,
      membershipId: user.membershipId,
    });
  }

  return NextResponse.json({ error: "Invalid credentials" }, { status: 401 });
}

memberships.route.ts:
import { NextResponse } from "next/server";
import { db } from "@/lib/db";
import { Membership } from "@/lib/definitios";
import { RowDataPacket } from "mysql2";

export async function GET(request: Request) {
  const { searchParams } = new URL(request.url);

  const page = parseInt(searchParams.get("page") || "1", 10);
  const pageSize = parseInt(searchParams.get("pageSize") || "10", 10);
  const searchTerm = searchParams.get("searchTerm") || null;

  try {
    const [result] = await db.query("CALL get_memberships(?, ?, ?)", [
      page,
      pageSize,
      searchTerm,
    ]);

    const rows = (result as RowDataPacket[][])[0];
    const totalEntries = (result as RowDataPacket[][])[1][0].totalEntries - 1;

    const memberships = rows as Membership[];

    return NextResponse.json({ memberships, totalEntries });
  } catch (error) {
    return NextResponse.json(
      { error: "Error al recuperar las membresías" },
      { status: 500 }
    );
  }
}

export async function PUT(request: Request) {
  try {
    const {
      id,
      name,
      benefits,
      price,
      discountThreeMonths,
      discountSixMonths,
      discountTwelveMonths,
      maxProjects,
      projectsFeatured,
    } = await request.json();

    if (
      id === undefined ||
      name === undefined ||
      benefits === undefined ||
      price === undefined ||
      maxProjects === undefined
    ) {
      return NextResponse.json(
        { error: "Faltan datos obligatorios" },
        { status: 400 }
