    const roles: Role[] = rolesRows.map((row: any) => ({
      id: row.id,
      name: row.name,
    }));

    const memberships: MembershipSummary[] = membershipsRows.map(
      (row: any) => ({
        id: row.id,
        name: row.name,
      })
    );

    const genders: Gender[] = gendersRows.map((row: any) => ({
      id: row.id,
      name: row.name,
    }));

    return NextResponse.json({
      users,
      totalEntries,
      roles,
      memberships,
      genders,
    });
  } catch (error) {
    return NextResponse.json(
      { error: "Error al recuperar los usuarios" },
      { status: 500 }
    );
  }
}

