"use client";

import React, { useState, useEffect, useRef } from "react";

export default function ContactForm() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [checked, setChecked] = useState(false);
  const [formState, setFormState] = useState<"fixed" | "bottom" | "default">(
    "default"
  );
  const [formOffset, setFormOffset] = useState<number | null>(null);
  const formRef = useRef<HTMLDivElement>(null);
  const [isLargeScreen, setIsLargeScreen] = useState<boolean>(false);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log("Datos enviados:", { name, email, phone });
  };

  useEffect(() => {
    const handleResize = () => {
      setIsLargeScreen(window.innerWidth >= 1024); // lg breakpoint
    };

    // Set initial state
    handleResize();

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    if (!isLargeScreen) {
      // Reset form state for small screens
      setFormState("default");
      return;
    }

    const handleScroll = () => {
      const formElement = formRef.current;
      const footerElement = document.querySelector("footer");
      const windowHeight = window.innerHeight;

      if (!formElement || !footerElement) return;

      const formHeight = formElement.offsetHeight;
      const formRect = formElement.getBoundingClientRect();
      const footerRect = footerElement.getBoundingClientRect();
      const pageHeight = document.documentElement.scrollHeight;

      // Calculate stop position
      const stopPosition = pageHeight - (800 + formHeight);

      // Adjust form to bottom if footer is visible
      if (footerRect.top <= windowHeight) {
        if (formState !== "bottom") {
          setFormOffset(stopPosition);
          setFormState("bottom");
        }
        return;
      }

      // Fix form when scrolling past a point
      if (window.scrollY > 400 && formRect.top <= 32) {
        if (formState !== "fixed") {
          setFormOffset(null);
          setFormState("fixed");
        }
        return;
      }

      // Reset to default at the top of the page
      if (window.scrollY <= 400) {
        if (formState !== "default") {
          setFormOffset(null);
          setFormState("default");
        }
        return;
      }

      // Ensure fixed state when scrolling up from bottom
      if (formState === "bottom" && window.scrollY > 400 && formRect.top > 32) {
        setFormOffset(null);
        setFormState("fixed");
      }
    };

    const onScroll = () => window.requestAnimationFrame(handleScroll);
    window.addEventListener("scroll", onScroll);

    return () => {
      window.removeEventListener("scroll", onScroll);
    };
  }, [formState, isLargeScreen]);

  const formStyles = {
    fixed: "fixed top-32 right-11 w-96 z-10",
    bottom: "absolute right-11 w-96",
    default: "relative w-full max-w-md mx-auto",
    smallScreen: "relative w-full max-w-md mx-auto",
  };

  const dynamicStyle =
    isLargeScreen && formState === "bottom" && formOffset !== null
      ? { top: `${formOffset}px` }
      : isLargeScreen && formState === "fixed"
      ? { top: "8rem", right: "2.75rem" }
      : {};

  return (
    <div
      ref={formRef}
      className={`${
        isLargeScreen ? formStyles[formState] : formStyles.smallScreen
      } bg-[#F4F1ED] text-[#5D4037] max-h-[80vh] overflow-y-auto border border-[#DAA520] p-6 rounded-lg`}
      style={dynamicStyle}
    >
      <h2 className="text-2xl font-bold mb-2 text-center text-[#8B4513]">
        Te llamamos
      </h2>
      <p className="text-sm text-center mb-4">
        Déjanos tus datos y pronto estaremos en contacto.
      </p>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="flex flex-col">
          <label className="font-medium mb-1">Nombre *</label>
          <input
            type="text"
            placeholder="Nombre completo"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="w-full px-3 py-2 rounded-md bg-white border border-[#DAA520] text-[#5D4037] outline-none"
            required
          />
        </div>
        <div className="flex flex-col">
          <label className="font-medium mb-1">Correo electrónico *</label>
          <input
            type="email"
            placeholder="Correo electrónico"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-3 py-2 rounded-md bg-white border border-[#DAA520] text-[#5D4037] outline-none"
            required
          />
        </div>
        <div className="flex flex-col">
          <label className="font-medium mb-1">Celular o Teléfono *</label>
          <input
            type="tel"
            placeholder="Número de contacto"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            className="w-full px-3 py-2 rounded-md bg-white border border-[#DAA520] text-[#5D4037] outline-none"
            required
          />
        </div>
        <div className="flex items-start">
          <input
            type="checkbox"
            checked={checked}
            onChange={() => setChecked(!checked)}
            className="form-checkbox mt-1 border-[#DAA520] text-[#DAA520] focus:ring-[#DAA520]"
            required
          />
          <label className="ml-2 text-xs">
            Acepto los{" "}
            <a href="#" className="text-[#DAA520] underline">
              términos y condiciones
            </a>
            .
          </label>
        </div>
        <button
          type="submit"
          className="w-full py-2 rounded-md font-semibold bg-[#DAA520] text-[#8B4513] border border-[#DAA520]"
        >
          Contáctame
        </button>
      </form>
    </div>
  );
}

createEditProject.basicProjectForm.tsx:
"use client";

import { useState } from "react";
import { propertyType, ProjectData } from "@/lib/definitios";
import {
  AiOutlineExclamationCircle,
  AiOutlineInfoCircle,
} from "react-icons/ai";
import StepNavigationButtons from "../../stepNavigationButtons";

type BasicProjectFormProps = {
  formData: ProjectData;
  onChange: (updatedData: Partial<ProjectData>) => void;
  onSubmit: (data: Partial<ProjectData>) => void;
  onPrevious: () => void;
  onNext: () => void;
  propertyTypes: propertyType[];
  currentStep: number;
  totalSteps: number;
  isProperty: boolean;
};

export default function BasicProjectForm({
  formData,
  onChange,
  propertyTypes,
  onPrevious,
  currentStep,
  totalSteps,
  isProperty,
  onNext,
}: BasicProjectFormProps) {
  const [errors, setErrors] = useState({
    nameError: "",
    shortDescriptionError: "",
    detailedDescriptionError: "",
    propertyTypeError: "",
    projectTypeError: "",
  });

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    onChange({ [e.target.name]: e.target.value });
    setErrors({ ...errors, [`${e.target.name}Error`]: "" });
  };

  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    onChange({ membership: e.target.checked ? 1004 : 1001 });
  };

  const handleSelectChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const { name, value } = e.target;
    const selectedId = parseInt(value, 10);

    if (name === "propertyType") {
      const selectedPropertyType = propertyTypes.find(
        (type) => type.id === selectedId
      );

      if (selectedPropertyType) {
        onChange({ propertyType: selectedPropertyType });
        setErrors((prevErrors) => ({
          ...prevErrors,
          propertyTypeError: "",
        }));
      }
    }

    if (name === "projectType") {
      if (selectedId === 2 || selectedId === 3) {
        onChange({
          projectType: {
            id: selectedId,
            name: selectedId === 2 ? "Venta" : "Arriendo",
          },
        });
        setErrors((prevErrors) => ({
          ...prevErrors,
          projectTypeError: "",
        }));
      } else {
        setErrors((prevErrors) => ({
          ...prevErrors,
          projectTypeError: "Seleccione un tipo de proyecto válido.",
        }));
      }
    }
  };

  const validateForm = () => {
    const newErrors = { ...errors };

    if (!formData.name) newErrors.nameError = "El nombre es obligatorio.";
    if (!formData.shortDescription)
      newErrors.shortDescriptionError = "El resumen breve es obligatorio.";
    if (!formData.detailedDescription)
      newErrors.detailedDescriptionError =
        "La descripción completa es obligatoria.";
    if (!formData.propertyType || !formData.propertyType.id)
      newErrors.propertyTypeError = "Seleccione el tipo de propiedad.";
    if ((!formData.projectType || !formData.projectType.id) && isProperty)
      newErrors.projectTypeError = "Seleccione la finlaidad de la propiedada";

    setErrors(newErrors);
    return Object.values(newErrors).every((error) => error === "");
  };

  const handleNext = (e?: React.FormEvent) => {
    e?.preventDefault();
    if (validateForm()) {
      onNext();
    }
  };

  return (
    <div className="container mx-auto max-w-2xl p-6 bg-premium-backgroundLight dark:bg-premium-backgroundDark rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold text-premium-primary dark:text-premium-primaryLight text-center mb-6">
        Datos Básicos
      </h2>
      <form onSubmit={handleNext} className="space-y-6">
        <div>
          <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-2">
            {isProperty ? "Nombre de la propiedad" : "Nombre del proyecto"}
            <span className="text-premium-textPrimary dark:text-premium-textPrimary -ml-1">
              *
            </span>
            <span className="group relative">
              <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
              <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                <p className="text-xs">
                  {isProperty
                    ? "Nombre de la propiedad que se mostrará al público."
                    : "Nombre del proyecto que se mostrará al público."}
                </p>
              </div>
            </span>
          </label>
          <input
            type="text"
            name="name"
            value={formData.name || ""}
            onChange={handleChange}
            maxLength={100}
            className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
              errors.nameError
                ? "border-red-500"
                : "border-premium-borderColor dark:border-premium-borderColorHover"
            }`}
            placeholder={
              !isProperty
                ? "Ingrese el nombre del proyecto"
                : "Ingrese el nombre de la propiedad"
            }
            required
          />
          {errors.nameError && (
            <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
              <AiOutlineExclamationCircle className="w-4 h-4" />
              {errors.nameError}
            </div>
          )}
        </div>

        <div
          className={`grid grid-cols-1 gap-4 ${
            isProperty ? "sm:grid-cols-2 " : ""
          }`}
        >
          <div>
            <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-2">
              Tipo de propiedad{" "}
              <span className="text-premium-textPrimary dark:text-premium-textPrimary -ml-1">
                *
              </span>
              <span className="group relative">
                <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                  <p className="text-xs">
                    {isProperty
                      ? "Seleccione el tipo de propiedad que más se ajuste."
                      : "Seleccione el tipo de propiedad que más se ajuste a su proyecto."}
                  </p>
                </div>
              </span>
            </label>
            <select
              name="propertyType"
              value={formData.propertyType?.id || ""}
              onChange={handleSelectChange}
              className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                errors.propertyTypeError
                  ? "border-red-500"
                  : "border-premium-borderColor dark:border-premium-borderColorHover"
              }`}
              required
            >
              <option value="">Seleccione el tipo de propiedad</option>
              {propertyTypes.map((type) => (
                <option key={type.id} value={type.id}>
                  {type.name}
                </option>
              ))}
            </select>
            {errors.propertyTypeError && (
              <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                <AiOutlineExclamationCircle className="w-4 h-4" />
                {errors.propertyTypeError}
              </div>
            )}
          </div>

          {isProperty && (
            <div>
              <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-2">
                Finalidad de la propiedad{" "}
                <span className="text-premium-textPrimary dark:text-premium-textPrimary -ml-1">
                  *
                </span>
                <span className="group relative">
                  <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                  <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                    <p className="text-xs">
                      Seleccione si la propiedad va estar en venta o en
                      arriendo.
                    </p>
                  </div>
                </span>
              </label>
              <select
                name="projectType"
                value={formData.projectType?.id || ""}
                onChange={handleSelectChange}
                className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                  errors.projectTypeError
                    ? "border-red-500"
                    : "border-premium-borderColor dark:border-premium-borderColorHover"
                }`}
                required
              >
                <option value="">Seleccione una opción</option>
                {[
                  { id: 3, name: "Arriendo" },
                  { id: 3, name: "Venta" },
                ].map((type) => (
                  <option key={type.id} value={type.id}>
                    {type.name}
                  </option>
                ))}
              </select>
              {errors.projectTypeError && (
                <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                  <AiOutlineExclamationCircle className="w-4 h-4" />
                  {errors.projectTypeError}
                </div>
              )}
            </div>
          )}
        </div>

        <div>
          <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-2">
            Resumen breve{" "}
            <span className="text-premium-textPrimary dark:text-premium-textPrimary -ml-1">
              *
            </span>
            <span className="group relative">
              <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
              <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                <p className="text-xs">
                  {isProperty
                    ? "Escriba un resumen breve que describa las principales características de la propiedad."
                    : "Escriba un resumen breve que describa las principales características del proyecto."}
                </p>
              </div>
            </span>
          </label>
          <textarea
            name="shortDescription"
            value={formData.shortDescription || ""}
            onChange={handleChange}
            maxLength={150}
            className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
              errors.shortDescriptionError
                ? "border-red-500"
                : "border-premium-borderColor dark:border-premium-borderColorHover"
            }`}
            placeholder="Agregue un resumen breve"
            rows={3}
            required
          />
          {errors.shortDescriptionError && (
            <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
              <AiOutlineExclamationCircle className="w-4 h-4" />
              {errors.shortDescriptionError}
            </div>
          )}
        </div>

        <div>
          <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-2">
            Descripción completa{" "}
            <span className="text-premium-textPrimary dark:text-premium-textPrimary -ml-1">
              *
            </span>
            <span className="group relative">
              <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
              <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                <p className="text-xs">
                  {isProperty
                    ? "Incluya una descripción detallada que explique las características principales de la propiedad."
                    : "Incluya una descripción detallada que explique las características principales del proyecto."}
                </p>
              </div>
            </span>
          </label>
          <textarea
            name="detailedDescription"
            value={formData.detailedDescription || ""}
            onChange={handleChange}
            maxLength={1500}
            className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
              errors.detailedDescriptionError
                ? "border-red-500"
                : "border-premium-borderColor dark:border-premium-borderColorHover"
            }`}
            placeholder="Agregue una descripción completa y detallada"
            rows={6}
            required
          />
          {errors.detailedDescriptionError && (
            <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
              <AiOutlineExclamationCircle className="w-4 h-4" />
              {errors.detailedDescriptionError}
            </div>
          )}
        </div>

        <div className="flex items-center gap-2">
          <input
            type="checkbox"
            id="membershipCheckbox"
            checked={formData.membership === 1004}
            onChange={handleCheckboxChange}
            className="w-4 h-4 text-premium-primary border-gray-300 rounded focus:ring-premium-primary"
          />
          <label
            htmlFor="membershipCheckbox"
            className="text-premium-textPrimary dark:text-premium-textPrimary text-sm"
          >
            Propio de la empresa
          </label>
        </div>

        <StepNavigationButtons
          currentStep={currentStep}
          totalSteps={totalSteps}
          onPrevious={onPrevious}
          onNext={handleNext}
        />
      </form>
    </div>
  );
}

createEditProject.detailsProjectForm.tsx:
"use client";

import { useState } from "react";
import StepNavigationButtons from "../../stepNavigationButtons";
import {
  CommonArea,
  NearbyService,
  HousingType,
  ProjectData,
} from "@/lib/definitios";
import {
  AiOutlineClose,
  AiOutlineExclamationCircle,
  AiOutlineInfoCircle,
} from "react-icons/ai";
import DatePicker from "react-datepicker";
import { formatNumber } from "@/utils/formatters";
import "react-datepicker/dist/react-datepicker.css";

type DetailsProjectFormProps = {
  formData: ProjectData;
  onChange: (updatedData: Partial<ProjectData>) => void;
  onSubmit: (data: Partial<ProjectData>) => void;
  onPrevious: () => void;
  onNext: () => void;
  currentStep: number;
  totalSteps: number;
  commonAreas: CommonArea[];
  nearbyServices: NearbyService[];
  housingTypes: HousingType[];
};

export default function DetailsProjectForm({
  formData,
  onChange,
  onPrevious,
  onNext,
  currentStep,
  totalSteps,
  commonAreas,
  nearbyServices,
  housingTypes,
}: DetailsProjectFormProps) {
  const [errors, setErrors] = useState({
    priceError: "",
    housingTypeError: "",
    availableUnitsError: "",
  });

  const handlePriceChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/[^0-9]/g, "");
    const numericValue = value ? parseInt(value, 10) : 0;

    onChange({ price: numericValue });
    if (numericValue > 0) {
      setErrors((prev) => ({ ...prev, priceError: "" }));
    }
  };

  const handleChangeText = (e: React.ChangeEvent<HTMLInputElement>) => {
    onChange({ [e.target.name]: e.target.value });
    setErrors({ ...errors, [`${e.target.name}Error`]: "" });
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type } = e.target;

    const updatedValue =
      type === "number" ? (value === "" ? "" : parseFloat(value)) : value;

    onChange({ [name]: updatedValue });

    if (value.trim()) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [`${name}Error`]: "",
      }));
    }
  };

  const handleDateChange = (date: Date | null) => {
    if (date) {
      onChange({ availableDate: date });
      setErrors((prev) => ({ ...prev, dateError: "" }));
    }
  };

  const handleSelectChange = (
    e: React.ChangeEvent<HTMLSelectElement>,
    type: "commonAreas" | "nearbyServices" | "housingType"
  ) => {
    const id = parseInt(e.target.value);

    if (type === "housingType") {
      const selectedHousingType = housingTypes.find((type) => type.id === id);
      onChange({ housingType: selectedHousingType });

      if (selectedHousingType) {
        setErrors((prevErrors) => ({ ...prevErrors, housingTypeError: "" }));
      }
    } else {
      const selectedItem =
        type === "commonAreas"
          ? commonAreas.find((area) => area.id === id)
          : nearbyServices.find((service) => service.id === id);

      if (selectedItem) {
        const updatedItems = [...(formData[type] || []), selectedItem];

        onChange({ [type]: updatedItems });
      }
    }
  };

  const handleRemoveTag = (
    id: number,
    type: "commonAreas" | "nearbyServices"
  ) => {
    const updatedItems = formData[type]?.filter((item) => item.id !== id) || [];
    onChange({ [type]: updatedItems });
  };

  const validateForm = () => {
    const newErrors = { ...errors };

    if (!formData.price) {
      newErrors.priceError = "El precio es obligatorio y debe ser mayor que 0.";
    }

    if (!formData.availableUnits && formData.projectType?.id === 1) {
      newErrors.availableUnitsError =
        "La cantidad de unidades disponibles es obligatoria";
    }

    if (
      (formData.propertyType?.id === 1001 ||
        formData.propertyType?.id === 1002) &&
      !formData.housingType
    )
      newErrors.housingTypeError = "Seleccione un tipo de vivienda.";

    setErrors(newErrors);
    return Object.values(newErrors).every((error) => error === "");
  };

  const handleNext = (e?: React.FormEvent) => {
    e?.preventDefault();
    if (validateForm()) {
      onNext();
    }
  };

  return (
    <div className="container mx-auto max-w-2xl p-6 bg-premium-backgroundLight dark:bg-premium-backgroundDark rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold text-premium-primary dark:text-premium-primaryLight text-center mb-6">
        {formData.projectType?.id === 1
          ? "Detalles del Proyecto"
          : "Detalles de la Propiedad"}
      </h2>

      <form onSubmit={handleNext} className="space-y-6">
        <div
          className={`grid grid-cols-1 gap-4 ${
            formData.projectType?.id === 1 ? "sm:grid-cols-2 " : ""
          }`}
        >
          <div className="w-full">
            <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
              Precio
              <span className="text-premium-textPrimary dark:text-premium-textPrimary">
                *
              </span>
              <span className="group relative">
                <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                  <p className="text-xs">
                    {formData.projectType?.id === 2 ||
                    formData.projectType?.id === 3
                      ? "Indique el precio de la propiedad."
                      : "Indique el precio por unidad del proyecto."}
                  </p>
                </div>
              </span>
            </label>
            <input
              type="text"
              name="price"
              value={formData.price ? formatNumber(formData.price) : ""}
              onChange={handlePriceChange}
              className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary ${
                errors.priceError
                  ? "border-red-500"
                  : "border-premium-borderColor dark:border-premium-borderColorHover"
              }`}
              placeholder="Ingrese el precio"
              required
            />
            {errors.priceError && (
              <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                <AiOutlineExclamationCircle className="w-4 h-4" />
                {errors.priceError}
              </div>
            )}
          </div>

          {formData.projectType?.id === 1 && (
            <div>
              <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
                Unidades Disponibles
                <span className="text-premium-textPrimary dark:text-premium-textPrimary">
                  *
                </span>
                <span className="group relative">
                  <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                  <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                    <p className="text-xs">
                      Especifique el número de unidades disponibles para este
                      proyecto.
                    </p>
                  </div>
                </span>
              </label>
              <input
                type="number"
                name="availableUnits"
                min={0}
                value={formData.availableUnits || ""}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary ${
                  errors.availableUnitsError
                    ? "border-red-500"
                    : "border-premium-borderColor dark:border-premium-borderColorHover"
                }`}
                placeholder="Número de unidades disponibles"
              />
              {errors.availableUnitsError && (
                <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                  <AiOutlineExclamationCircle className="w-4 h-4" />
                  {errors.availableUnitsError}
                </div>
              )}
            </div>
          )}
        </div>

        <div
          className={`grid grid-cols-1 gap-4 ${
            formData.propertyType?.id === 1001 ||
            formData.propertyType?.id === 1002
              ? "sm:grid-cols-2"
              : ""
          }`}
        >
          {(formData.propertyType?.id === 1001 ||
            formData.propertyType?.id === 1002) && (
            <div>
              <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
                Tipo de Vivienda
                <span className="text-premium-textPrimary dark:text-premium-textPrimary">
                  *
                </span>
                <span className="group relative">
                  <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                  <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                    <p className="text-xs">Seleccione el tipo de vivienda.</p>
                  </div>
                </span>
              </label>
              <select
                name="housingType"
                value={formData.housingType?.id || ""}
                className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                  errors.housingTypeError
                    ? "border-red-500"
                    : "border-premium-borderColor dark:border-premium-borderColorHover"
                }`}
                onChange={(e) => handleSelectChange(e, "housingType")}
                required
              >
                <option value="">Seleccione un tipo de vivienda</option>
                {housingTypes.map((type) => (
                  <option key={type.id} value={type.id}>
                    {type.name}
                  </option>
                ))}
              </select>
              {errors.housingTypeError && (
                <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                  <AiOutlineExclamationCircle className="w-4 h-4" />
                  {errors.housingTypeError}
                </div>
              )}
            </div>
          )}

          <div className="w-full">
            <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
              Fecha Estimada de Entrega
              <span className="group relative">
                <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                  <p className="text-xs">
                    Seleccione la fecha estimada para la entrega del proyecto
                    (mes/año).
                  </p>
                </div>
              </span>
            </label>
            <DatePicker
              selected={formData.availableDate}
              onChange={handleDateChange}
              showMonthYearPicker
              minDate={new Date()}
              dateFormat="MM/yyyy"
              placeholderText="mm/yyyy"
              className="w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary border-premium-borderColor dark:border-premium-borderColorHover"
              wrapperClassName="w-full"
            />
          </div>
        </div>

        {formData.propertyType?.id === 1002 &&
          (formData.projectType?.id === 2 ||
            formData.projectType?.id === 3) && (
            <div>
              <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-2">
                Nombre del conjunto
                <span className="group relative">
                  <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                  <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                    <p className="text-xs">
                      Nombre del conjunto residencial (si aplica).
                    </p>
                  </div>
                </span>
              </label>
              <input
                type="text"
                name="complexName"
                value={formData.complexName || ""}
                onChange={handleChangeText}
                maxLength={100}
                className="w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary border-premium-borderColor dark:border-premium-borderColorHover"
                placeholder="Ingrese el nombre del proyecto"
              />
            </div>
          )}

        <div>
          <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
            Áreas Comunes
            <span className="group relative">
              <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
              <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                <p className="text-xs">
                  {formData.projectType?.id === 2 ||
                  formData.projectType?.id === 3
                    ? "Seleccione las áreas comunes disponibles en la propiedad."
                    : "Seleccione las áreas comunes disponibles en el proyecto."}
                </p>
              </div>
            </span>
          </label>
          <select
            onChange={(e) => handleSelectChange(e, "commonAreas")}
            className="w-full px-3 py-2 border rounded-md bg-premium-background border-premium-borderColor dark:border-premium-borderColorHover dark:bg-premium-backgroundLight text-premium-textPrimary"
          >
            <option value="">Seleccione un área común</option>
            {commonAreas.map((area) => (
              <option key={area.id} value={area.id}>
                {area.name}
              </option>
            ))}
          </select>
          <div className="flex flex-wrap mt-2">
            {formData.commonAreas?.map((area) => (
              <div
                key={area.id}
                className="bg-premium-primary dark:bg-premium-primaryLight text-white px-3 py-1 m-1 rounded-full flex items-center"
              >
                {area.name}
                <button
                  className="ml-2 text-white hover:text-premium-textPrimary"
                  onClick={() => handleRemoveTag(area.id, "commonAreas")}
                >
                  <AiOutlineClose className="w-4 h-4" />
                </button>
              </div>
            ))}
          </div>
        </div>

        <div>
          <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
            Servicios Cercanos
            <span className="group relative">
              <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
              <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                <p className="text-xs">
                  {formData.projectType?.id === 2 ||
                  formData.projectType?.id === 3
                    ? "Seleccione los servicios cercanos a la propiedad."
                    : "Seleccione los servicios cercanos al proyecto."}
                </p>
              </div>
            </span>
          </label>
          <select
            onChange={(e) => handleSelectChange(e, "nearbyServices")}
            className="w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary border-premium-borderColor dark:border-premium-borderColorHover"
          >
            <option value="">Seleccione un servicio cercano</option>
            {nearbyServices.map((service) => (
              <option key={service.id} value={service.id}>
                {service.name}
              </option>
            ))}
          </select>
          <div className="flex flex-wrap mt-2">
            {formData.nearbyServices?.map((service) => (
              <div
                key={service.id}
                className="bg-premium-primary dark:bg-premium-primaryLight text-white px-3 py-1 m-1 rounded-full flex items-center"
              >
                {service.name}
                <button
                  className="ml-2 text-white hover:text-premium-textPrimary"
                  onClick={() => handleRemoveTag(service.id, "nearbyServices")}
                >
                  <AiOutlineClose className="w-4 h-4" />
                </button>
              </div>
            ))}
          </div>
        </div>

        <StepNavigationButtons
          currentStep={currentStep}
          totalSteps={totalSteps}
          onPrevious={onPrevious}
          onNext={handleNext}
        />
      </form>
    </div>
  );
}

createEditProject.featuresProjectForm.tsx:
"use client";

import { useState } from "react";
import {
  AiOutlineExclamationCircle,
  AiOutlineInfoCircle,
} from "react-icons/ai";
import StepNavigationButtons from "../../stepNavigationButtons";
import { ProjectData } from "@/lib/definitios";

type FeaturesProjectFormProps = {
  formData: ProjectData;
  onChange: (updatedData: Partial<ProjectData>) => void;
  onSubmit: (data: Partial<ProjectData>) => void;
  onPrevious: () => void;
  onNext: () => void;
  currentStep: number;
  totalSteps: number;
};

export default function FeaturesProjectForm({
  formData,
  onChange,
  onPrevious,
  onNext,
  currentStep,
  totalSteps,
}: FeaturesProjectFormProps) {
  const [errors, setErrors] = useState({
    builtAreaError: "",
    totalAreaError: "",
    housingTypeError: "",
    bathroomsError: "",
    bedroomsError: "",
    lobbiesError: "",
    freeHeightError: "",
    widthError: "",
    lengthError: "",
    towersError: "",
    socioeconomicLevelError: "",
    yearBuiltError: "",
  });

  const handleSelectChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const { name, value } = e.target;

    const selectedValue = parseInt(value);

    onChange({ [name]: selectedValue });

    if (!isNaN(selectedValue)) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [`${name}Error`]: "",
      }));
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type } = e.target;

    const updatedValue =
      type === "number" ? (value === "" ? "" : parseFloat(value)) : value;

    onChange({ [name]: updatedValue });

    if (value.trim()) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [`${name}Error`]: "",
      }));
    }
  };

  const validateForm = () => {
    const newErrors = { ...errors };

    if (!formData.builtArea)
      newErrors.builtAreaError = "El área construida es obligatoria.";

    if (!formData.totalArea)
      newErrors.totalAreaError = "El área total es obligatoria.";

    if (shouldShowField("bathrooms") && !formData.bathrooms)
      newErrors.bathroomsError = "El número de baños es obligatorio.";

    if (shouldShowField("bedrooms") && !formData.bedrooms)
      newErrors.bedroomsError = "El número de habitaciones es obligatorio.";

    if (shouldShowField("lobbies") && !formData.lobbies)
      newErrors.lobbiesError = "El número de salas de estar es obligatorio.";

    if (shouldShowField("freeHeight") && !formData.freeHeight)
      newErrors.freeHeightError = "La altura libre es obligatoria.";

    if (shouldShowField("width") && !formData.width)
      newErrors.widthError = "El ancho es obligatorio.";

    if (shouldShowField("length") && !formData.length)
      newErrors.lengthError = "El largo es obligatorio.";

    if (
      shouldShowField("towers") &&
      !formData.towers &&
      formData.projectType?.id === 1
    )
      newErrors.towersError = "El número de torres es obligatorio.";

    if (
      shouldShowField("socioeconomicLevel") &&
      !formData.socioeconomicLevel &&
      (formData.projectType?.id === 2 || formData.projectType?.id === 3)
    )
      newErrors.socioeconomicLevelError = "El estrato es obligatorio";

    if (
      shouldShowField("yearBuilt") &&
      !formData.yearBuilt &&
      (formData.projectType?.id === 2 || formData.projectType?.id === 3)
    )
      newErrors.yearBuiltError = "El año de contrucción es obligatorio";

    setErrors(newErrors);
    return Object.values(newErrors).every((error) => error === "");
  };

  const handleNext = (e?: React.FormEvent) => {
    e?.preventDefault();
    if (validateForm()) {
      onNext();
    }
  };

  const shouldShowField = (field: string) => {
    const propertyTypeId = formData.propertyType?.id;

    if (
      field === "socioecnomicLevel" ||
      field === "yearBuilt" ||
      field === "bedrooms" ||
      field === "storageUnits" ||
      field === "balcony" ||
      field === "laundryArea" ||
      field === "customizationOptions"
    ) {
      return propertyTypeId === 1001 || propertyTypeId === 1002;
    }
    if (field === "terrace" || field === "garden") {
      return propertyTypeId === 1001;
    }
    if (field === "bathrooms" || field === "lobbies") {
      return (
        propertyTypeId === 1001 ||
        propertyTypeId === 1002 ||
        propertyTypeId === 1004
      );
    }
    if (field === "elevators") {
      return (
        propertyTypeId === 1002 ||
        propertyTypeId === 1003 ||
        propertyTypeId === 1004
      );
    }
    if (field === "towers" || field === "floorNumber") {
      return propertyTypeId == 1002;
    }
    if (field === "freeHeight") {
      return (
        propertyTypeId === 1004 ||
        propertyTypeId === 1003 ||
        propertyTypeId === 1005
      );
    }
    if (field === "width" || field === "length") {
      return (
        propertyTypeId === 1005 ||
        propertyTypeId === 1006 ||
        propertyTypeId === 1007
      );
    }
    if (field === "heavyParking") {
      return propertyTypeId === 1005;
    }
    return true;
  };

  return (
    <div className="container mx-auto max-w-2xl p-6 bg-premium-backgroundLight dark:bg-premium-backgroundDark rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold text-premium-primary dark:text-premium-primaryLight text-center mb-6">
        {formData.projectType?.id === 1
          ? "Características del Proyecto"
          : "Características de la Propiedad"}
      </h2>

      <form onSubmit={handleNext} className="space-y-6">
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          <div>
            <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
              Área Total (m²){" "}
              <span className="text-premium-textPrimary dark:text-premium-textPrimary">*</span>
              <span className="group relative">
                <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                  <p className="text-xs">
                    {formData.projectType?.id === 2 ||
                    formData.projectType?.id === 3
                      ? "Ingrese el área total de la propiedad en metros cuadrados."
                      : "Ingrese el área total del proyecto en metros cuadrados."}
                  </p>
                </div>
              </span>
            </label>
            <input
              type="number"
              name="totalArea"
              min={0}
              value={formData.totalArea || ""}
              onChange={handleChange}
              className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                errors.totalAreaError
                  ? "border-red-500"
                  : "border-premium-borderColor dark:border-premium-borderColorHover"
              }`}
              placeholder="Área total"
              required
            />
            {errors.totalAreaError && (
              <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                <AiOutlineExclamationCircle className="w-5 h-5" />
                {errors.totalAreaError}
              </div>
            )}
          </div>

          <div>
            <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
              Área Construida (m²){" "}
              <span className="text-premium-textPrimary dark:text-premium-textPrimary">*</span>
              <span className="group relative">
                <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                  <p className="text-xs">
                    {formData.projectType?.id === 2 ||
                    formData.projectType?.id === 3
                      ? "Ingrese el área construida de la propiedad en metros cuadrados, considerando únicamente las áreas cubiertas."
                      : "Ingrese el área construida del proyecto en metros cuadrados, considerando únicamente las áreas cubiertas."}
                  </p>
                </div>
              </span>
            </label>
            <input
              type="number"
              name="builtArea"
              min={0}
              value={formData.builtArea || ""}
              onChange={handleChange}
              className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                errors.builtAreaError
                  ? "border-red-500"
                  : "border-premium-borderColor dark:border-premium-borderColorHover"
              }`}
              placeholder="Área construida"
              required
            />
            {errors.builtAreaError && (
              <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                <AiOutlineExclamationCircle className="w-5 h-5" />
                {errors.builtAreaError}
              </div>
            )}
          </div>

          {shouldShowField("socioeconomicLevel") &&
            (formData.projectType?.id === 2 ||
              formData.projectType?.id === 3) && (
              <div>
                <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
                  Nivel Socioeconómico{" "}
                  <span className="text-premium-textPrimary dark:text-premium-textPrimary">
                    *
                  </span>
                  <span className="group relative">
                    <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                    <div className="absolute hidden group-hover:block bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                      <p className="text-xs">
                        Seleccione el nivel socioeconómico asignado a la
                        propiedad según la clasificación oficial del sector.
                      </p>
                    </div>
                  </span>
                </label>
                <select
                  name="socioeconomicLevel"
                  value={formData.socioeconomicLevel || ""}
                  onChange={handleSelectChange}
                  className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                    errors.socioeconomicLevelError
                      ? "border-red-500"
                      : "border-premium-borderColor"
                  }`}
                >
                  <option value="" disabled>
                    Seleccione un nivel
                  </option>
                  {[1, 2, 3, 4, 5, 6].map((level) => (
                    <option key={level} value={level}>
                      Estrato {level}
                    </option>
                  ))}
                </select>
                {errors.socioeconomicLevelError && (
                  <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                    <AiOutlineExclamationCircle className="w-5 h-5" />
                    {errors.socioeconomicLevelError}
                  </div>
                )}
              </div>
            )}

          {shouldShowField("yearBuilt") &&
            (formData.projectType?.id === 2 ||
              formData.projectType?.id === 3) && (
              <div>
                <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
                  Año de Contrucción{" "}
                  <span className="text-premium-textPrimary dark:text-premium-textPrimary">
                    *
                  </span>
                  <span className="group relative">
                    <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                    <div className="absolute hidden group-hover:block bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                      <p className="text-xs">
                        Seleccione el año en que la propiedad fue construida o
                        su estructura original terminada.
                      </p>
                    </div>
                  </span>
                </label>
                <select
                  name="yearBuilt"
                  value={formData.yearBuilt || ""}
                  onChange={handleSelectChange}
                  className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                    errors.yearBuiltError
                      ? "border-red-500"
                      : "border-premium-borderColor"
                  }`}
                >
                  <option value="" disabled>
                    Seleccione el año
                  </option>
                  {Array.from(
                    { length: new Date().getFullYear() - 1950 + 1 },
                    (_, i) => 1950 + i
                  ).map((year) => (
                    <option key={year} value={year}>
                      {year}
                    </option>
                  ))}
                </select>
                {errors.yearBuiltError && (
                  <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                    <AiOutlineExclamationCircle className="w-5 h-5" />
                    {errors.yearBuiltError}
                  </div>
                )}
              </div>
            )}

          {shouldShowField("bedrooms") && (
            <div>
              <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
                Número de Habitaciones{" "}
                <span className="text-premium-textPrimary dark:text-premium-textPrimary">
                  *
                </span>
                <span className="group relative">
                  <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                  <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                    <p className="text-xs">
                      {formData.projectType?.id === 2 ||
                      formData.projectType?.id === 3
                        ? "Especifique cuántas habitaciones tiene la propiedad."
                        : "Especifique cuántas habitaciones tendrá el proyecto."}
                    </p>
                  </div>
                </span>
              </label>
              <input
                type="number"
                name="bedrooms"
                min={0}
                value={formData.bedrooms || ""}
                onChange={handleChange}
                placeholder="Número de habitaciones"
                className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                  errors.bedroomsError
                    ? "border-red-500"
                    : "border-premium-borderColor dark:border-premium-borderColorHover"
                }`}
                required
              />
              {errors.bedroomsError && (
                <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                  <AiOutlineExclamationCircle className="w-5 h-5" />
                  {errors.bedroomsError}
                </div>
              )}
            </div>
          )}

          {shouldShowField("bathrooms") && (
            <div>
              <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
                Número de Baños{" "}
                <span className="text-premium-textPrimary dark:text-premium-textPrimary">
                  *
                </span>
                <span className="group relative">
                  <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                  <div className="absolute hidden group-hover:flex flex-col items-center bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                    <p className="text-xs">
                      {formData.projectType?.id === 2 ||
                      formData.projectType?.id === 3
                        ? "Indique la cantidad total de baños que tiene la propiedad."
                        : "Indique la cantidad total de baños que estarán incluidos en el proyecto."}
                    </p>
                  </div>
                </span>
              </label>
              <input
                type="number"
                name="bathrooms"
                min={0}
                value={formData.bathrooms || ""}
                onChange={handleChange}
                placeholder="Número de baños"
                className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
