createEditProject.imagesProjectForm.tsx:
"use client";

import { useState, ChangeEvent, useEffect } from "react";
import { ProjectData, ImageType, Media } from "@/lib/definitios";
import ImageUploadSection from "./ImageUploadSection";
import StepNavigationButtons from "../../stepNavigationButtons";
import ModalAlert from "../../modals/modalAlert";

type UploadImagesFormProps = {
  formData: ProjectData;
  onChange: (updatedData: Partial<ProjectData>) => void;
  onPrevious: () => void;
  onNext: () => void;
  currentStep: number;
  totalSteps: number;
  imagesTypes: ImageType[];
  onSubmit: (media: Media[]) => void;
};

export default function ImagesProjectForm({
  formData,
  onPrevious,
  onNext,
  currentStep,
  totalSteps,
  imagesTypes,
  onSubmit,
}: UploadImagesFormProps) {
  const [images, setImages] = useState<Record<string, File[]>>({});
  const [imageDescriptions, setImageDescriptions] = useState<
    Record<string, string[]>
  >({});
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [expandedSections, setExpandedSections] = useState<
    Record<string, boolean>
  >({});
  const [imageTags, setImageTags] = useState<Record<string, string[]>>({});
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");

  useEffect(() => {
    const initialExpandedState: Record<string, boolean> = {};
    [...imagesTypes, ...(formData.commonAreas || [])].forEach((item) => {
      initialExpandedState[item.name] = false;
    });
    setExpandedSections(initialExpandedState);
  }, [imagesTypes, formData]);

  const toggleSection = (category: string) => {
    setExpandedSections((prev) => ({
      ...prev,
      [category]: !prev[category],
    }));
  };

  const handleImageChange = (
    category: string,
    event: ChangeEvent<HTMLInputElement>,
    maxImages: number
  ) => {
    const maxFileSize = 3 * 1024 * 1024;
    const selectedFiles = Array.from(event.target.files || []);
    const validFiles: File[] = [];
    const invalidFiles: File[] = [];

    selectedFiles.forEach((file) => {
      if (file.size <= maxFileSize) {
        validFiles.push(file);
      } else {
        invalidFiles.push(file);
      }
    });

    if (invalidFiles.length > 0) {
      setAlertMessage(
        "Algunas imágenes superan el límite de tamaño de 3MB y no se cargarán."
      );
      setShowAlert(true);
    }

    const currentImages = images[category] || [];
    const currentDescriptions = imageDescriptions[category] || [];

    if (currentImages.length + validFiles.length > maxImages) {
      setErrors((prev) => ({
        ...prev,
        [category]: `Máximo ${maxImages} imágenes permitidas para esta categoría.`,
      }));
      return;
    }

    setImages((prev) => ({
      ...prev,
      [category]: [...currentImages, ...validFiles],
    }));
    setErrors((prev) => ({ ...prev, [category]: "" }));

    setImageTags((prev) => ({
      ...prev,
      [category]: [...(prev[category] || []), ...validFiles.map(() => "")],
    }));

    setImageDescriptions((prev) => ({
      ...prev,
      [category]: [...currentDescriptions, ...validFiles.map(() => "")],
    }));

    event.target.value = "";
  };

  const handleRemoveImage = (category: string, index: number) => {
    const updatedImages = [...(images[category] || [])];
    updatedImages.splice(index, 1);

    const updatedTags = [...(imageTags[category] || [])];
    updatedTags.splice(index, 1);

    const updatedDescriptions = [...(imageDescriptions[category] || [])];
    updatedDescriptions.splice(index, 1);

    setImages((prev) => ({ ...prev, [category]: updatedImages }));
    setImageTags((prev) => ({ ...prev, [category]: updatedTags }));
    setImageDescriptions((prev) => ({
      ...prev,
      [category]: updatedDescriptions,
    }));
  };

  const handleDescriptionChange = (
    category: string,
    index: number,
    description: string
  ) => {
    setImageDescriptions((prev) => {
      const updatedDescriptions = [...(prev[category] || [])];
      updatedDescriptions[index] = description;
      return {
        ...prev,
        [category]: updatedDescriptions,
      };
    });

    if (description.length > 150) {
      setErrors((prev) => ({
        ...prev,
        [`${category}-description`]:
          "La descripción no puede superar los 150 caracteres.",
      }));
    } else {
      setErrors((prev) => ({
        ...prev,
        [`${category}-description`]: "",
      }));
    }
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    imagesTypes
      .filter((type) => type.isRequired)
      .forEach((item) => {
        if (!images[item.name] || images[item.name].length === 0) {
          newErrors[
            item.name
          ] = `Debe subir al menos una imagen para ${item.name}.`;
        }
      });

    formData.commonAreas?.forEach((area) => {
      if (!images[area.name] || images[area.name].length === 0) {
        newErrors[
          area.name
        ] = `Debe subir al menos una imagen para ${area.name}.`;
      }
    });

    Object.entries(images).forEach(([category, files]) => {
      files.forEach((_, index) => {
        const tag = imageTags[category]?.[index];
        const description = imageDescriptions[category]?.[index];
        const imageType = imagesTypes.find((type) => type.name === category);

        if (!tag || tag.trim() === "") {
          newErrors[`${category}-tag-${index}`] =
            "El nombre de la imagen es obligatorio.";
        }

        if (
          imageType?.id === 1005 &&
          (!description || description.trim() === "")
        ) {
          newErrors[`${category}-description-${index}`] =
            "La descripción es obligatoria para esta imagen.";
        }
      });
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const transformImagesToMedia = (): Media[] => {
    const mediaArray: Media[] = [];

    Object.entries(images).forEach(([type, files]) => {
      const imageType = imagesTypes.find((imgType) => imgType.name === type);
      const commonArea = formData.commonAreas?.find(
        (area) => area.name === type
      );

      files.forEach((file, index) => {
        mediaArray.push({
          tag: imageTags[type][index] || "",
          file,
          description: imageDescriptions[type]?.[index] || "",
          idType: imageType ? imageType.id : commonArea?.id || 0,
          type: type,
          category: imageType ? "imageType" : "commonArea",
        });
      });
    });

    return mediaArray;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      const mediaData = transformImagesToMedia();
      onSubmit(mediaData);
      onNext();
    }
  };

  const handleTagChange = (category: string, index: number, tag: string) => {
    setImageTags((prev) => {
      const updatedTags = { ...prev };
      const categoryTags = [...(updatedTags[category] || [])];
      categoryTags[index] = tag;
      updatedTags[category] = categoryTags;
      return updatedTags;
    });
  };

  return (
    <div className="container mx-auto max-w-2xl p-6 bg-premium-backgroundLight dark:bg-premium-backgroundDark rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold text-premium-primary dark:text-premium-primaryLight text-center mb-6">
        {formData.projectType?.id === 1
          ? "Subir Imágenes del Proyecto"
          : "Subir Imágenes de la Propiedad"}
      </h2>

      <form
        onSubmit={(e) => {
          e.preventDefault();
        }}
        className="space-y-6"
      >
        {imagesTypes.map((type) => (
          <ImageUploadSection
            imageType={type}
            expanded={expandedSections[type.name]}
            images={images[type.name] || []}
            tags={imageTags[type.name] || []}
            descriptions={imageDescriptions[type.name] || []}
            error={errors[type.name] || null}
            errors={Object.keys(errors)
              .filter((key) => key.startsWith(`${type.name}-`))
              .reduce((acc, key) => {
                acc[key] = errors[key];
                return acc;
              }, {} as Record<string, string>)}
            onToggleExpand={() => toggleSection(type.name)}
            onImageChange={(e) =>
              handleImageChange(type.name, e, type.maxImagesAllowed)
            }
            onRemoveImage={(index) => handleRemoveImage(type.name, index)}
            onTagChange={(index, newTag) =>
              handleTagChange(type.name, index, newTag)
            }
            onDescriptionChange={(index, newDescription) =>
              handleDescriptionChange(type.name, index, newDescription)
            }
          />
        ))}

        {formData.commonAreas && (
          <h3 className="text-xl font-bold text-premium-primary text-center dark:text-premium-primaryLight mb-6">
            Subir imágenes de las áreas comunes
          </h3>
        )}

        {formData.commonAreas?.map((area) => (
          <ImageUploadSection
            key={area.name}
            imageType={{
              id: area.id,
              name: area.name,
              maxImagesAllowed: 5,
              isRequired: true,
            }}
            expanded={expandedSections[area.name] || false}
            images={images[area.name] || []}
            tags={imageTags[area.name] || []}
            descriptions={imageDescriptions[area.name] || []}
            error={errors[area.name] || null}
            errors={Object.keys(errors)
              .filter((key) => key.startsWith(`${area.name}-`))
              .reduce((acc, key) => {
                acc[key] = errors[key];
                return acc;
              }, {} as Record<string, string>)}
            onToggleExpand={() => toggleSection(area.name)}
            onImageChange={(e) => handleImageChange(area.name, e, 5)}
            onRemoveImage={(index) => handleRemoveImage(area.name, index)}
            onTagChange={(index, newTag) =>
              handleTagChange(area.name, index, newTag)
            }
            onDescriptionChange={(index, newDescription) =>
              handleDescriptionChange(area.name, index, newDescription)
            }
          />
        ))}

        <StepNavigationButtons
          currentStep={currentStep}
          totalSteps={totalSteps}
          onPrevious={onPrevious}
          onNext={handleSubmit}
        />
      </form>

      <ModalAlert
        title="Advertencia"
        message={alertMessage}
        isOpen={showAlert}
        onClose={() => setShowAlert(false)}
      />
    </div>
  );
}

createEditProject.ImageUploadSection.tsx:
import { AiOutlineDown, AiOutlineExclamationCircle } from "react-icons/ai";
import ImageWithTag from "./ImageWithTag";
import { ImageType } from "@/lib/definitios";

type ImageUploadSectionProps = {
  imageType: ImageType;
  expanded: boolean;
  images: File[];
  tags: string[];
  descriptions: string[];
  error: string | null;
  errors: Record<string, string>;
  onToggleExpand: () => void;
  onImageChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
  onRemoveImage: (index: number) => void;
  onTagChange: (index: number, tag: string) => void;
  onDescriptionChange: (index: number, description: string) => void;
};

export default function ImageUploadSection({
  imageType,
  expanded,
  images,
  tags,
  descriptions,
  error,
  errors,
  onToggleExpand,
  onImageChange,
  onRemoveImage,
  onTagChange,
  onDescriptionChange,
}: ImageUploadSectionProps) {
  return (
    <div
      className={`pb-1 rounded-md bg-premium-backgroundAlt dark:bg-premium-backgroundDarkAlt hover:bg-premium-backgroundLight hover:dark:bg-premium-backgroundLight ${
        error ? "border border-red-500" : "border-none"
      }`}
    >
      <div
        className="flex justify-between items-center cursor-pointer p-4"
        onClick={onToggleExpand}
      >
        <h4 className="text-lg font-semibold text-premium-textPrimary">
          {imageType.name}
        </h4>
        <AiOutlineDown
          className={`${
            expanded ? "rotate-180" : ""
          } transition-transform duration-300 text-premium-textPrimary dark:text-premium-textSecondary`}
        />
      </div>

      {imageType.description && (
        <p className="text-sm text-gray-600 dark:text-gray-400 -mt-3 mb-4 px-4 cursor-pointer">
          {imageType.description}
        </p>
      )}

      {expanded && (
        <div className="px-4 pb-4">
          <input
            type="file"
            accept="image/*"
            multiple
            onChange={onImageChange}
            className={`w-full px-3 py-2 rounded-lg bg-premium-backgroundDark dark:bg-premium-background text-premium-textPrimary mt-2 ${
              error ? "border-red-500" : "border-none"
            }`}
          />
          <p className="mt-1 ml-2 text-sm text-gray-600 dark:text-gray-400">
            {images.length} archivo(s) subido(s)
          </p>

          {error && (
            <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
              <AiOutlineExclamationCircle className="w-5 h-5" />
              {error}
            </div>
          )}

          <div className="flex flex-wrap mt-2">
            {images.map((file, index) => (
              <ImageWithTag
                key={index}
                file={file}
                tag={tags[index] || ""}
                description={descriptions[index] || ""}
                imageTypeId={imageType.id}
                onRemove={() => onRemoveImage(index)}
                onTagChange={(newTag) => onTagChange(index, newTag)}
                onDescriptionChange={(newDescription) =>
                  onDescriptionChange(index, newDescription)
                }
                error={errors[`${imageType.name}-tag-${index}`]}
                descriptionError={
                  errors[`${imageType.name}-description-${index}`]
                }
              />
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

createEditProject.ImageWithTag.tsx:
import { ChangeEvent, useMemo } from "react";
import { AiOutlineClose } from "react-icons/ai";

type ImageWithTagProps = {
  file: File;
  tag: string;
  description: string;
  imageTypeId: number;
  onRemove: () => void;
  onTagChange: (newTag: string) => void;
  onDescriptionChange: (newDescription: string) => void;
  error?: string;
  descriptionError?: string;
};

export default function ImageWithTag({
  file,
  tag,
  description,
  imageTypeId,
  onRemove,
  onTagChange,
  onDescriptionChange,
  error,
  descriptionError,
}: ImageWithTagProps) {
  const imageUrl = useMemo(() => URL.createObjectURL(file), [file]);

  const handleTagChange = (e: ChangeEvent<HTMLInputElement>) => {
    onTagChange(e.target.value);
  };

  const handleDescriptionChange = (e: ChangeEvent<HTMLTextAreaElement>) => {
    onDescriptionChange(e.target.value);
  };

  return (
    <div className="relative bg-premium-backgroundLight dark:bg-premium-backgroundDark border border-gray-300 dark:border-gray-700 rounded-md p-3 w-48">
      <div
        className="relative w-full h-32 bg-cover bg-center rounded-md mb-4"
        style={{
          backgroundImage: `url(${imageUrl})`,
        }}
      >
        <button
          type="button"
          className="absolute top-2 right-2 bg-gray-400 text-white rounded-full p-1 hover:bg-gray-600 transition"
          onClick={onRemove}
          aria-label="Eliminar imagen"
        >
          <AiOutlineClose className="w-4 h-4" />
        </button>
      </div>

      <label className="block text-sm font-medium text-premium-textPrimary dark:text-premium-textSecondary mb-2">
        Nombre de imagen
      </label>
      <input
        type="text"
        value={tag}
        onChange={handleTagChange}
        placeholder="Nombre"
        maxLength={50}
        aria-invalid={!!error}
        className={`w-full px-3 py-2 text-sm border rounded-md bg-white dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary focus:outline-none focus:ring-2 focus:ring-premium-primary ${
          error ? "border-red-500 focus:ring-red-500" : "border-gray-300"
        }`}
      />
      {error && (
        <p className="text-red-500 text-xs mt-2">Etiqueta obligatoria.</p>
      )}

      {imageTypeId === 1005 && (
        <>
          <label className="block text-sm font-medium text-premium-textPrimary dark:text-premium-textSecondary mt-4 mb-2">
            Descripción corta
          </label>
          <textarea
            value={description}
            onChange={handleDescriptionChange}
            placeholder="Descripción"
            maxLength={100}
            className={`w-full h-20 px-3 py-2 text-sm border rounded-md bg-white dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary focus:outline-none focus:ring-2 ${
              descriptionError
                ? "border-red-500 focus:ring-red-500"
                : "border-gray-300"
            }`}
          />
          {descriptionError && (
            <p className="text-red-500 text-xs mt-1">{descriptionError}</p>
          )}
        </>
      )}
    </div>
  );
}

createEditProject.locationProjectForm.tsx:
"use client";

import { useState, useRef, useEffect } from "react";
import { LatLngTuple } from "leaflet";
import {
  AiOutlineExclamationCircle,
  AiOutlineClose,
  AiOutlineInfoCircle,
} from "react-icons/ai";
import StepNavigationButtons from "../../stepNavigationButtons";
import LocationMap from "../locationMap";
import { useAddressSearch } from "@/app/hooks/useAddressSearch";
import { ProjectData, City, Departament } from "@/lib/definitios";

interface LocationProjectFormProps {
  formData: ProjectData;
  onChange: (updatedData: Partial<ProjectData>) => void;
  onSubmit: (data: Partial<ProjectData>) => void;
  onPrevious: () => void;
  onNext: () => void;
  currentStep: number;
  totalSteps: number;
  departaments: Departament[];
  cities: City[];
}

export default function LocationProjectForm({
  formData,
  onChange,
  onPrevious,
  onNext,
  currentStep,
  totalSteps,
  departaments,
  cities,
}: LocationProjectFormProps) {
  const [errors, setErrors] = useState({
    departamentError: "",
    cityError: "",
    addressError: "",
  });

  const { results, loading, error, searchAddress } = useAddressSearch();
  const [isListVisible, setIsListVisible] = useState(false);
  const listRef = useRef<HTMLUListElement>(null);

  const handleAddressInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    onChange({ address: value });

    if (value.trim()) {
      searchAddress(value);
      setErrors((prevErrors) => ({ ...prevErrors, addressError: "" }));
      setIsListVisible(true);
    }
  };

  const handleDepartamentChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const departamentId = parseInt(e.target.value);
    const selectedDepartament = departaments.find(
      (dep) => dep.id === departamentId
    );

    if (selectedDepartament) {
      onChange({
        city: {
          id: 0,
          name: "",
          departament: selectedDepartament,
        },
      });

      setErrors((prevErrors) => ({ ...prevErrors, departamentError: "" }));
    }
  };

  const handleCityChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const cityId = parseInt(e.target.value);
    const selectedCity = cities.find((city) => city.id === cityId);

    if (selectedCity) {
      onChange({
        city: selectedCity,
      });

      setErrors((prevErrors) => ({ ...prevErrors, cityError: "" }));
    }
  };

  const handleAddressSelect = (
    lat: string,
    lon: string,
    displayName: string
  ) => {
    onChange({
      address: displayName,
      latitude: parseFloat(lat),
      longitude: parseFloat(lon),
    });

    setErrors((prevErrors) => ({ ...prevErrors, addressError: "" }));
  };

  const validateForm = () => {
    const newErrors = { ...errors };

    if (!formData.city?.departament?.id) {
      newErrors.departamentError = "El departamento es obligatorio.";
    }
    if (!formData.city?.id) {
      newErrors.cityError = "La ciudad es obligatoria.";
    }
    if (!formData.address?.trim()) {
      newErrors.addressError = "La dirección es obligatoria.";
    }

    setErrors(newErrors);
    return Object.values(newErrors).every((error) => error === "");
  };

  const handleNext = (e?: React.FormEvent) => {
    e?.preventDefault();
    if (validateForm()) {
      onNext();
    }
  };

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (listRef.current && !listRef.current.contains(event.target as Node)) {
        setIsListVisible(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="container mx-auto max-w-2xl p-6 bg-premium-backgroundLight dark:bg-premium-backgroundDark rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold text-premium-primary dark:text-premium-primaryLight text-center mb-6">
        {formData.projectType?.id
          ? "Ubicación del Proyecto"
          : "Ubicación de la Propiedad"}
      </h2>

      <form className="space-y-6">
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          <div>
            <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
              Departamento
              <span className="text-premium-textPrimary dark:text-premium-textPrimary">
                *
              </span>
              <span className="group relative">
                <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                <div className="absolute hidden group-hover:block bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                  <p className="text-xs">
                    {formData.projectType?.id === 2 ||
                    formData.projectType?.id === 3
                      ? "Seleccione el departamento donde se encuentra la propiedad."
                      : "Seleccione el departamento donde se encuentra el proyecto."}
                  </p>
                </div>
              </span>
            </label>
            <select
              name="departament"
              value={formData.city?.departament?.id || ""}
              onChange={handleDepartamentChange}
              className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                errors.departamentError
                  ? "border-red-500"
                  : "border-premium-borderColor"
              }`}
            >
              <option value="">Seleccione un departamento</option>
              {departaments.map((dep) => (
                <option key={dep.id} value={dep.id}>
                  {dep.name}
                </option>
              ))}
            </select>
            {errors.departamentError && (
              <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                <AiOutlineExclamationCircle className="w-5 h-5" />
                {errors.departamentError}
              </div>
            )}
          </div>

          <div>
            <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
              Ciudad
              <span className="text-premium-textPrimary dark:text-premium-textPrimary">
                *
              </span>
              <span className="group relative">
                <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
                <div className="absolute hidden group-hover:block bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                  <p className="text-xs">
                    {formData.projectType?.id === 2 ||
                    formData.projectType?.id === 3
                      ? "Seleccione la ciudad correspondiente la propiedad."
                      : "Seleccione la ciudad correspondiente al proyecto."}
                  </p>
                </div>
              </span>
            </label>
            <select
              name="city"
              value={formData.city?.id || ""}
              onChange={handleCityChange}
              className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                errors.cityError ? "border-red-500" : "border-borderColor"
              }`}
            >
              <option value="">Seleccione una ciudad</option>
              {cities
                .filter(
                  (city) =>
                    city.departament.id === formData.city?.departament?.id
                )
                .map((city) => (
                  <option key={city.id} value={city.id}>
                    {city.name}
                  </option>
                ))}
            </select>
            {errors.cityError && (
              <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
                <AiOutlineExclamationCircle className="w-5 h-5" />
                {errors.cityError}
              </div>
            )}
          </div>
        </div>

        <div className="relative">
          <label className="text-premium-textPrimary dark:text-premium-textPrimary mb-2 flex items-center gap-1">
            Dirección
            <span className="text-premium-textPrimary dark:text-premium-textPrimary">
              *
            </span>
            <span className="group relative">
              <AiOutlineInfoCircle className="w-4 h-4 cursor-pointer text-gray-500 dark:text-gray-400" />
              <div className="absolute hidden group-hover:block bg-premium-backgroundLight dark:bg-premium-backgroundDark text-premium-textPrimary dark:text-premium-textPrimary border border-premium-borderColor dark:border-premium-borderColorHover rounded-md shadow-md p-2 w-64 mt-2 z-10">
                <p className="text-xs">
                  {formData.projectType?.id === 2 ||
                  formData.projectType?.id === 3
                    ? "Ingrese la dirección de la propiedad."
                    : "Ingrese la dirección del proyecto."}
                </p>
              </div>
            </span>
          </label>

          <div className="flex items-center">
            <input
              type="text"
              name="address"
              value={formData.address || ""}
              onChange={handleAddressInput}
              disabled={!formData.city || !formData.city.id}
              className={`w-full px-3 py-2 border rounded-md bg-premium-background dark:bg-premium-backgroundLight text-premium-textPrimary dark:text-premium-textPrimary ${
                errors.addressError
                  ? "border-red-500"
                  : "border-premium-borderColor"
              }`}
              placeholder="Ingrese la dirección"
            />
            {formData.address && (
              <button
                type="button"
                className="absolute right-3 text-gray-500 hover:text-gray-700"
                onClick={() => onChange({ address: "" })}
              >
                <AiOutlineClose className="w-5 h-5" />
              </button>
            )}
          </div>

          {errors.addressError && (
            <div className="text-red-500 text-xs flex items-center gap-2 mt-1">
              <AiOutlineExclamationCircle className="w-5 h-5" />
              {errors.addressError}
            </div>
          )}
        </div>

        <LocationMap
          coordinates={
            [
              formData.latitude || 4.5709,
              formData.longitude || -74.2973,
            ] as LatLngTuple
          }
          onLocationSelect={(coords) =>
            onChange({ latitude: coords[0], longitude: coords[1] })
          }
        />

        <StepNavigationButtons
          currentStep={currentStep}
          totalSteps={totalSteps}
          onPrevious={onPrevious}
          onNext={handleNext}
        />
      </form>
    </div>
  );
}

createEditProject.progressBar.tsx:
import {
  FaInfoCircle,
  FaMapMarkerAlt,
  FaBuilding,
  FaListAlt,
  FaImage,
} from "react-icons/fa";

type ProgressBarProps = {
  currentStep: number;
};

const steps = [
  { icon: FaInfoCircle },
  { icon: FaMapMarkerAlt },
  { icon: FaBuilding },
  { icon: FaListAlt },
  { icon: FaImage },
];

export default function ProgressBar({ currentStep }: ProgressBarProps) {
  return (
    <div className="flex justify-center items-center w-full max-w-xl mx-auto">
      {steps.map((step, index) => {
        const Icon = step.icon;
        const isLastStep = index === steps.length - 1;
        const isActive = currentStep >= index;

        return (
          <div
            key={index}
            className={`flex items-center ${isLastStep ? "" : "flex-1"}`}
          >
            <div
              className={`w-12 h-12 z-10 rounded-full flex items-center justify-center transition-colors duration-300 ${
                isActive
                  ? "bg-premium-primary text-white dark:bg-premium-primaryLight"
                  : "bg-premium-backgroundDark text-premium-textSecondary dark:bg-premium-backgroundLight dark:text-premium-textPlaceholder"
              }`}
            >
              <Icon size={25} />
            </div>

            {!isLastStep && (
              <div
                className={`h-1 transition-colors duration-300 ${
                  currentStep > index
                    ? "bg-premium-borderColor dark:bg-premium-borderColorHover"
                    : "bg-premium-backgroundDark dark:bg-premium-backgroundLight"
                }`}
                style={{
                  width: "calc(100% - 2.25rem)",
                  marginLeft: "-4px",
                }}
              ></div>
            )}
          </div>
        );
      })}
    </div>
  );
}

