filter.filterOptionWithSlider.tsx:
import { useState, useEffect } from "react";
import { FaChevronDown } from "react-icons/fa";

type FilterOptionWithSliderProps = {
  label: string;
  icon: React.ReactNode;
  value: number;
  min: number;
  max: number;
  step: number;
  onChange: (value: number) => void;
  isOpen: boolean;
  setIsOpen: (isOpen: boolean) => void;
  category: string;
  prefixText: string;
  suffixText: string;
};

export default function FilterOptionWithSlider({
  label,
  icon,
  value,
  min,
  max,
  step,
  onChange,
  isOpen,
  setIsOpen,
  category,
  prefixText,
  suffixText,
}: FilterOptionWithSliderProps) {
  const [sliderValue, setSliderValue] = useState(value);

  const handleSliderChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = parseInt(event.target.value, 10);
    setSliderValue(newValue);
  };

  const handleSliderComplete = () => {
    onChange(sliderValue);
  };

  useEffect(() => {
    setSliderValue(value);
  }, [value]);

  const formatDisplayValue = (value: number) => {
    if (value >= 1000000000) {
      const newValue = value / 1000000000;
      return `${newValue.toFixed(3)}M`;
    } else if (value >= 1000000) {
      const newValue = value / 1000000;
      return Number.isInteger(newValue)
        ? `${newValue}M`
        : `${newValue.toFixed(1)}M`;
    } else if (value >= 100000) {
      const newValue = value / 1000;
      return Number.isInteger(newValue)
        ? `${newValue}K`
        : `${newValue.toFixed(1)}K`;
    } else if (value >= 1000) {
      const newValue = value / 1000;
      return newValue.toFixed(3);
    } else {
      return value.toString();
    }
  };

  return (
    <>
      <div
        className="flex items-center justify-between cursor-pointer mb-4 transition-transform duration-200 transform hover:scale-105 hover:font-semibold text-premium-textPrimary dark:text-premium-textPrimary"
        onClick={() => setIsOpen(!isOpen)}
      >
        <div
          className={`flex items-center space-x-2 text-lg font-medium ${
            sliderValue > 1
              ? "text-premium-primary dark:text-premium-primaryLight"
              : "text-premium-textPrimary dark:text-premium-textPrimary"
          }`}
        >
          {icon}
          <span>{label}</span>
        </div>
        <FaChevronDown
          className={`transition-transform duration-300 transform ${
            isOpen ? "rotate-180" : ""
          } text-premium-textPrimary dark:text-premium-textPrimary`}
        />
      </div>
      {isOpen && (
        <div className="flex flex-col items-center space-y-2 mb-6 px-4">
          <input
            type="range"
            min={min}
            max={max}
            step={sliderValue > 10000000 ? 10000000 : step}
            value={sliderValue}
            onChange={handleSliderChange}
            onMouseUp={handleSliderComplete}
            onTouchEnd={handleSliderComplete}
            className="w-full h-1 bg-premium-primary rounded-full appearance-none cursor-pointer dark:bg-premium-primaryLight"
          />
          <style jsx>{`
            input[type="range"]::-webkit-slider-thumb {
              appearance: none;
              height: 20px;
              width: 20px;
              background-color: var(--primary);
              border-radius: 50%;
              cursor: pointer;
              border: 2px solid var(--backgroundAlt);
            }

            input[type="range"]::-moz-range-thumb {
              height: 20px;
              width: 20px;
              background-color: var(--primary);
              border-radius: 50%;
              cursor: pointer;
              border: 2px solid var(--backgroundAlt);
            }

            input[type="range"]::-ms-thumb {
              height: 20px;
              width: 20px;
              background-color: var(--primary);
              border-radius: 50%;
              cursor: pointer;
              border: 2px solid var(--backgroundAlt);
            }
          `}</style>
          {sliderValue > 1 && (
            <div className="text-center text-base text-premium-textPrimary dark:text-premium-textPrimary">
              <span>{prefixText} </span>
              <span className="font-bold text-2xl">
                {category === "price" ? "$" : "+"}
                {formatDisplayValue(sliderValue)}
              </span>
              <span> {suffixText}</span>
            </div>
          )}
        </div>
      )}
    </>
  );
}

filter.projectFilter.tsx:
"use client";

import { useState, useEffect, useRef } from "react";
import axios from "axios";
import FilterOption from "./filterOption";
import FilterNumericCounter from "./filterNumericCounter";
import FilterOptionWithSlider from "./filterOptionWithSlider";
import {
  FaCity,
  FaBuilding,
  FaHome,
  FaDollarSign,
  FaBed,
  FaToilet,
  FaCouch,
  FaWarehouse,
  FaConciergeBell,
  FaTimes,
  FaCrown,
  FaRulerHorizontal,
} from "react-icons/fa";
import {
  City,
  HousingType,
  propertyType,
  NearbyService,
  CommonArea,
  MembershipSummary,
} from "@/lib/definitios";

type Category =
  | "cities"
  | "propertyTypes"
  | "housingTypes"
  | "commonAreas"
  | "nearbyServices"
  | "memberships"
  | "bedrooms"
  | "bathrooms"
  | "lobbies"
  | "price"
  | "area";

type ProjectFilterProps = {
  selectedButtons: Record<Category, number[]>;
  setSelectedButtons: React.Dispatch<
    React.SetStateAction<Record<Category, number[]>>
  >;
  setFilterOpen: (open: boolean) => void;
  priceRange: { min: number; max: number };
  areaRange: { min: number; max: number };
};

export default function ProjectFilter({
  selectedButtons,
  setSelectedButtons,
  setFilterOpen,
  priceRange,
  areaRange,
}: ProjectFilterProps) {
  const [metadata, setMetadata] = useState({
    cities: [] as City[],
    commonAreas: [] as CommonArea[],
    housingTypes: [] as HousingType[],
    nearbyServices: [] as NearbyService[],
    propertyTypes: [] as propertyType[],
    memberships: [] as MembershipSummary[],
  });

  const [isOpen, setIsOpen] = useState<Record<Category, boolean>>({
    cities: false,
    propertyTypes: false,
    housingTypes: false,
    commonAreas: false,
    nearbyServices: false,
    memberships: false,
    bedrooms: false,
    bathrooms: false,
    lobbies: false,
    price: false,
    area: false,
  });

  const [isFixed, setIsFixed] = useState(false);
  const filterRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("/api/projects/metadata");
        setMetadata(response.data);
      } catch (error) {
        console.error("Error fetching metadata:", error);
      }
    };
    fetchData();
  }, []);

  const toggleOpen = (category: Category) => {
    setIsOpen((prevState) => ({
      ...prevState,
      [category]: !prevState[category],
    }));
  };

  const [bottomReached, setBottomReached] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const filterTop = filterRef.current?.getBoundingClientRect().top ?? 0;
      const filterBottom =
        filterRef.current?.getBoundingClientRect().bottom ?? 0;
      const scrollY = window.scrollY;
      const bottomOffset = 30;

      if (scrollY >= filterTop + bottomOffset && !bottomReached) {
        setIsFixed(true);
      } else {
        setIsFixed(false);
      }

      const pageHeight = document.documentElement.scrollHeight;
      const windowHeight = window.innerHeight;
      if (windowHeight + scrollY >= pageHeight - 200) {
        setBottomReached(true);
        setIsFixed(false);
      } else {
        setBottomReached(false);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [bottomReached]);

  const updateFilterValue = (
    category: Category,
    newValue: number | number[]
  ) => {
    setSelectedButtons((prevSelected) => ({
      ...prevSelected,
      [category]: Array.isArray(newValue) ? newValue : [newValue],
    }));
  };

  const changeCount = (
    category: "bedrooms" | "bathrooms" | "lobbies",
    increment: boolean
  ) => {
    setSelectedButtons((prevSelected) => ({
      ...prevSelected,
      [category]: [
        Math.max(
          0,
          Math.min(prevSelected[category][0] + (increment ? 1 : -1), 3)
        ),
      ],
    }));
  };

  const handleToggleItem = (category: Category, id: number) => {
    setSelectedButtons((prevSelected) => {
      const selectedItems = prevSelected[category];
      return {
        ...prevSelected,
        [category]: selectedItems.includes(id)
          ? selectedItems.filter((itemId) => itemId !== id)
          : [...selectedItems, id],
      };
    });
  };

  const resetFilters = () => {
    setSelectedButtons({
      cities: [],
      propertyTypes: [],
      housingTypes: [],
      commonAreas: [],
      nearbyServices: [],
      memberships: [],
      bedrooms: [0],
      bathrooms: [0],
      lobbies: [0],
      price: [1],
      area: [1],
    });
  };

  return (
    <div
      ref={filterRef}
      className={`w-full sm:w-72 md:w-80 bg-premium-background dark:bg-premium-backgroundDark shadow-md rounded-lg py-4 transition-all duration-300 ease-in-out ${
        isFixed ? "sticky top-32 lg:top-20 z-10" : ""
      }`}
    >
      <div className="mt-1 space-y-6">
        <div className="flex items-center justify-between rounded-md pl-2 pr-6">
          <button
            onClick={resetFilters}
            className="font-medium text-lg py-1 px-4 text-premium-textPrimary dark:text-premium-textPrimary transition-transform duration-200 transform hover:scale-110 hover:font-semibold"
          >
            Borrar filtros
          </button>
          <div
            className="flex items-center cursor-pointer text-premium-textPrimary dark:text-premium-textPrimary"
            onClick={() => setFilterOpen(false)}
          >
            <FaTimes className="transition-transform duration-200 transform hover:scale-125" />
          </div>
        </div>

        <hr className="border-premium-borderColor dark:border-premium-borderColorHover" />

        <div className="space-y-4 px-3 py-1 sm:max-h-[65vh] sm:overflow-y-auto overflow-x-hidden">
          {/* Filtros principales */}
          {[
            {
              label: "Ciudades",
              icon: <FaCity />,
              items: metadata.cities,
              category: "cities" as Category,
            },
            {
              label: "Tipos de Inmueble",
              icon: <FaBuilding />,
              items: metadata.propertyTypes,
              category: "propertyTypes" as Category,
            },
            {
              label: "Tipos de Vivienda",
              icon: <FaHome />,
              items: metadata.housingTypes,
              category: "housingTypes" as Category,
            },
            {
              label: "Áreas Comunes",
              icon: <FaWarehouse />,
              items: metadata.commonAreas,
              category: "commonAreas" as Category,
            },
            {
              label: "Servicios Cercanos",
              icon: <FaConciergeBell />,
              items: metadata.nearbyServices,
              category: "nearbyServices" as Category,
            },
            {
              label: "Membresía",
              icon: <FaCrown />,
              items: metadata.memberships,
              category: "memberships" as Category,
            },
          ].map(({ label, icon, items, category }) => (
            <FilterOption
              key={category}
              label={label}
              icon={icon}
              items={items}
              selectedItems={selectedButtons[category]}
              onToggleItem={(id) => handleToggleItem(category, id)}
              isOpen={isOpen[category]}
              onToggleOpen={() => toggleOpen(category)}
            />
          ))}

          {/* Filtros con sliders */}
          {[
            {
              label: "Precio",
              icon: <FaDollarSign />,
              value: selectedButtons.price[0] || 0,
              min: priceRange.min || 0,
              max: priceRange.max || 0,
              step: 100000,
              category: "price" as Category,
              prefixText: "Desde",
              suffixText: "COP",
            },
            {
              label: "Área",
              icon: <FaRulerHorizontal />,
              value: selectedButtons.area[0] || 0,
              min: areaRange.min || 0,
              max: areaRange.max || 0,
              step: 100,
              category: "area" as Category,
              prefixText: "Desde",
              suffixText: "m²",
            },
          ].map(
            ({
              label,
              icon,
              value,
              min,
              max,
              step,
              category,
              prefixText,
              suffixText,
            }) => (
              <FilterOptionWithSlider
                key={category}
                label={label}
                icon={icon}
                value={value}
                min={min}
                max={max}
                step={step}
                onChange={(newValue) => updateFilterValue(category, newValue)}
                isOpen={isOpen[category]}
                setIsOpen={() => toggleOpen(category)}
                category={category}
                prefixText={prefixText}
                suffixText={suffixText}
              />
            )
          )}

          {/* Contadores */}
          {[
            {
              label: "Nº de habitaciones",
              icon: <FaBed />,
              category: "bedrooms" as "bedrooms" | "bathrooms" | "lobbies",
            },
            {
              label: "Nº de baños",
              icon: <FaToilet />,
              category: "bathrooms" as "bedrooms" | "bathrooms" | "lobbies",
            },
            {
              label: "Nº de salas de estar",
              icon: <FaCouch />,
              category: "lobbies" as "bedrooms" | "bathrooms" | "lobbies",
            },
          ].map(({ label, icon, category }) => (
            <FilterNumericCounter
              key={category}
              label={label}
              icon={icon}
              isOpen={isOpen[category]}
              setIsOpen={() => toggleOpen(category)}
              value={selectedButtons[category][0]}
              increment={() => changeCount(category, true)}
              decrement={() => changeCount(category, false)}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

