      <div className="h-4 bg-gray-300 rounded w-3/4 mb-2"></div>
      <div className="h-4 bg-gray-300 rounded w-2/4"></div>
    </div>
  );
}

projects.projectCard.tsx:
import { useState, useEffect } from "react";
import { FaTimes, FaEdit, FaTrash } from "react-icons/fa";
import { ProjectMedia } from "@/lib/definitios";
import Link from "next/link";
import Image from "next/image";

type ProjectCardProps = {
  images: ProjectMedia[];
  name: string;
  location: string;
  price: number;
  area: number;
  isFromMap: boolean;
  showActions: boolean;
  onClose?: (() => void) | null;
  url: string;
  urlEdit: string;
};

export default function ProjectCard({
  images,
  name,
  location,
  price,
  area,
  isFromMap,
  showActions,
  onClose,
  url,
  urlEdit,
}: ProjectCardProps) {
  const [currentImage, setCurrentImage] = useState(0);
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isHovered) {
      interval = setInterval(
        () => setCurrentImage((prev) => (prev + 1) % images.length),
        1000
      );
    }
    return () => clearInterval(interval);
  }, [isHovered, images.length]);

  return (
    <div className="bg-transparent dark:bg-transparent rounded-lg transform transition-transform hover:scale-105 duration-300">
      <Link href={url} target="_blank" rel="noopener noreferrer">
        <div
          className={`relative max-w-xs shadow-premium-secondary dark:shadow-white rounded-lg overflow-hidden bg-premium-secondary dark:bg-premium-secondaryDark cursor-pointer shadow-md dark:shadow-md ${
            isFromMap
              ? "z-50 hover:scale-105 transition-transform duration-300"
              : "h-[370px] z-0"
          }`}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => {
            setIsHovered(false);
            setCurrentImage(0);
          }}
        >
          {showActions && (
            <div className="absolute top-2 right-2 flex space-x-2 z-50">
              <Link
                href={urlEdit}
                className="p-2 rounded-full bg-blue-500 hover:bg-blue-600 text-white transition-colors"
              >
                <FaEdit />
              </Link>
              <button className="p-2 rounded-full bg-red-500 text-white hover:bg-red-600 transition-colors">
                <FaTrash />
              </button>
            </div>
          )}

          <div className="bg-premium-background dark:bg-premium-secondaryDark rounded-lg h-full flex flex-col relative">
            {onClose && (
              <button
                onClick={(e) => {
                  e.preventDefault();
                  onClose();
                }}
                className="absolute top-2 right-2 z-20 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-75 transition-all"
              >
                <FaTimes />
              </button>
            )}

            <div
              className={`relative w-full z-0 overflow-hidden ${
                !isFromMap ? "flex-shrink-0 h-full" : "h-72"
              }`}
            >
              {images.map((img, index) => (
                <Image
                  key={index}
                  src={img.url}
                  alt={img.tag}
                  fill
                  className={`w-full h-full object-cover absolute top-0 left-0 transition-opacity duration-1000 ease-in-out ${
                    index === currentImage ? "opacity-100" : "opacity-0"
                  }`}
                />
              ))}
            </div>

            <div className="absolute top-2 left-2 bg-black bg-opacity-75 text-white text-sm px-2 py-1 rounded-full z-50">
              ${price.toLocaleString()}
            </div>

            <div className="px-2 pt-16 pb-2 z-20 absolute bottom-0 left-0 right-0 bg-gradient-to-t from-premium-secondaryDark dark:from-black via-premium-secondaryDark dark:via-gray-800 to-transparent">
              <h3 className="text-lg font-semibold text-white leading-tight break-words line-clamp-1">
                {name}
              </h3>
              <p className="text-sm text-gray-300">{location}</p>
              {area > 0 && (
                <p className="text-xs text-gray-300">Área: {area} m²</p>
              )}
            </div>
          </div>
        </div>
      </Link>
    </div>
  );
}

projects.projectsContainer.tsx:
"use client";

import { ProjectSummary } from "@/lib/definitios";
import ProjectFilter from "@/app/ui/projects/filter/projectFilter";
import ProjectCard from "@/app/ui/projects/projectCard";
import ProjectCardSkeleton from "./skeleton/projectCardSkeleton";

interface ProjectsContainerProps {
  projects: ProjectSummary[];
  totalEntries: number;
  isLoading: boolean;
  fetchMoreProjects: () => void;
  filterOpen: boolean;
  showMap: boolean;
  setFilterOpen: (open: boolean) => void;
  priceRange: { min: number; max: number };
  areaRange: { min: number; max: number };
  selectedButtons: Record<string, number[]>;
  setSelectedButtons: React.Dispatch<
    React.SetStateAction<Record<string, number[]>>
  >;
  isProperty: boolean;
}

export default function ProjectsContainer({
  projects,
  totalEntries,
  isLoading,
  fetchMoreProjects,
  filterOpen,
  showMap,
  setFilterOpen,
  priceRange,
  areaRange,
  selectedButtons,
  setSelectedButtons,
  isProperty,
}: ProjectsContainerProps) {
  const skeletonCount = 8;

  return (
    <div
      className={`-mt-10 mb-10 flex flex-col sm:flex-row sm:justify-between ${
        filterOpen ? "sm:space-x-6 gap-4" : ""
      } py-4 px-6 lg:px-8 xl:px-20`}
    >
      {!showMap && (
        <div
          className={`flex flex-col items-center w-full sm:px-10 sm:w-72 ${
            filterOpen ? "block" : "hidden"
          }`}
        >
          <ProjectFilter
            selectedButtons={selectedButtons}
            setSelectedButtons={setSelectedButtons}
            setFilterOpen={setFilterOpen}
            priceRange={priceRange}
            areaRange={areaRange}
          />
        </div>
      )}

      <div className="flex flex-col w-full">
        <div
          className={`grid gap-x-4 gap-y-6 mb-8 ${
            filterOpen
              ? "grid-cols-1 md:grid-cols-2 lg:grid-cols-3 2xl:grid-cols-4"
              : "grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 2xl:grid-cols-5"
          }`}
        >
          {isLoading
            ? Array.from({ length: skeletonCount }).map((_, index) => (
                <div
                  key={`skeleton-${index}`}
                  className="flex justify-center items-center relative"
                >
                  <div className="w-full max-w-xs">
                    <ProjectCardSkeleton />
                  </div>
                </div>
              ))
            : projects.map((project) => (
                <div
                  key={project.id}
                  className="flex justify-center items-center relative"
                >
                  <div className="w-full max-w-xs">
                    <ProjectCard
                      key={project.id}
                      images={project.projectMedia}
                      name={project.name}
                      location={`${project.city.name}, ${project.city.departament.name}`}
                      price={project.price}
                      area={project.totalArea}
                      isFromMap={false}
                      showActions={true}
                      onClose={null}
                      url={
                        isProperty
                          ? `/inmobiliaria/${project.name
                              .toLowerCase()
                              .replace(/\s+/g, "-")}`
                          : `/proyectos/${project.name
                              .toLowerCase()
                              .replace(/\s+/g, "-")}`
                      }
                      urlEdit={
                        isProperty
                          ? `/admin/propiedades/${project.name
                              .toLowerCase()
                              .replace(/\s+/g, "-")}`
                          : `/admin/proyectos/${project.name
                              .toLowerCase()
                              .replace(/\s+/g, "-")}`
                      }
                    />
                  </div>
                </div>
              ))}
        </div>

        {projects.length < totalEntries && (
          <button
            onClick={fetchMoreProjects}
            disabled={isLoading}
            className="mt-6 px-6 py-2 rounded-lg shadow-lg bg-premium-primary dark:bg-premium-primaryLight text-white hover:bg-premium-primaryDark dark:hover:bg-premium-primaryDark transition-colors self-center"
          >
            {isLoading ? "Cargando..." : "Mostrar más"}
          </button>
        )}
      </div>
    </div>
  );
}

projects.projectsMap.tsx:
"use client";

import dynamic from "next/dynamic";
import { useEffect, useState } from "react";
import "leaflet/dist/leaflet.css";
import { ProjectSummary } from "@/lib/definitios";
import ProjectCard from "./projectCard";
import PriceMarker from "./priceMarker";

const MapContainer = dynamic(
  () => import("react-leaflet").then((mod) => mod.MapContainer),
  { ssr: false }
);

const TileLayer = dynamic(
  () => import("react-leaflet").then((mod) => mod.TileLayer),
  { ssr: false }
);

type ProjectsMapProps = {
  projects: ProjectSummary[];
};

export default function ProjectsMap({ projects }: ProjectsMapProps) {
  const [isClient, setIsClient] = useState(false);
  const [selectedProject, setSelectedProject] =
    useState<ProjectSummary | null>(null);

  useEffect(() => {
    setIsClient(true);
  }, []);

  if (!isClient) {
    return null;
  }

  const center: [number, number] = [4.5709, -74.2973];

  const handleCloseCard = () => {
    setSelectedProject(null);
  };

  return (
    <div className="relative w-full h-full">
      <MapContainer
        key={projects.length}
        center={center}
        zoom={7}
        minZoom={4}
        className="w-full h-full relative z-0"
      >
        <TileLayer
          url="https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}{r}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>'
        />

        {projects.map((project) => (
          <PriceMarker
            key={project.id}
            position={[project.latitude, project.longitude]}
            price={project.price}
            onClick={() => setSelectedProject(project)}
            isSelected={selectedProject?.id === project.id}
          />
        ))}
      </MapContainer>

      {selectedProject && (
        <div className="absolute top-4 left-1/2 transform -translate-x-1/2 sm:left-auto sm:right-4 sm:translate-x-0 z-50 bg-premium-backgroundLight dark:bg-premium-backgroundDark shadow-lg rounded-lg w-72">
          <ProjectCard
            images={[
              "/images/image2.jpg",
              "/images/image3.jpg",
              "/images/image4.jpg",
            ]}
            name={selectedProject.name}
            location={`${selectedProject.city.name}, ${selectedProject.city.departament.name}`}
            price={selectedProject.price}
            area={0}
            idMembership={selectedProject.membership}
            isFromMap={true}
            showActions={true}
            onClose={handleCloseCard}
            url={`/admin/proyectos/${selectedProject.id}`}
          />
        </div>
      )}
    </div>
  );
}

projects.shareButton.tsx:
"use client";

import { useState, useEffect } from "react";
import {
  FaShareAlt,
  FaWhatsapp,
  FaFacebook,
  FaTwitter,
  FaTelegram,
  FaTimes,
} from "react-icons/fa";

type ShareButtonProps = {
  message: string;
};

export default function ShareButton({ message }: ShareButtonProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [url, setUrl] = useState("");

  useEffect(() => {
    if (typeof window !== "undefined") {
      setUrl(window.location.href);
    }
  }, []);

  const handleShare = (platform: string) => {
    let shareUrl = "";
    const encodedMessage = encodeURIComponent(`${message} ${url}`);

    switch (platform) {
      case "whatsapp":
        shareUrl = `https://api.whatsapp.com/send?text=${encodedMessage}`;
        break;
      case "facebook":
        shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(
          url
        )}`;
        break;
      case "twitter":
        shareUrl = `https://twitter.com/intent/tweet?url=${encodeURIComponent(
          url
        )}&text=${encodeURIComponent(message)}`;
        break;
      case "telegram":
        shareUrl = `https://t.me/share/url?url=${encodeURIComponent(
          url
        )}&text=${encodedMessage}`;
        break;
      default:
        return;
    }

    window.open(
      shareUrl,
      "_blank",
      "width=600,height=400,scrollbars=no,toolbar=no,location=no,status=no,menubar=no"
    );
  };

  return (
    <div className="relative">
      <button
        onClick={() => setIsOpen(true)}
        className="p-2 rounded-full text-[#8B4513] hover:text-[#DAA520] transition duration-300"
        aria-label="Compartir"
      >
        <FaShareAlt size={20} />
      </button>
      {isOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white relative rounded-lg px-4 pb-4 pt-2 w-64">
            <button
              onClick={() => setIsOpen(false)}
              className="absolute top-3 right-3 text-gray-500 hover:text-gray-700 transition duration-200"
              aria-label="Cerrar modal"
            >
              <FaTimes size={20} />
            </button>
            <h2
              className="text-center text-lg font-semibold mb-4"
              style={{ color: "#8B4513" }}
            >
              Compartir en:
            </h2>
            <div className="flex justify-around items-center">
              <button
                onClick={() => handleShare("whatsapp")}
                className="text-green-500 hover:scale-110 transition duration-200"
                aria-label="Compartir en WhatsApp"
              >
                <FaWhatsapp size={30} />
              </button>
              <button
                onClick={() => handleShare("facebook")}
                className="text-blue-500 hover:scale-110 transition duration-200"
                aria-label="Compartir en Facebook"
              >
                <FaFacebook size={30} />
              </button>
              <button
                onClick={() => handleShare("twitter")}
                className="text-blue-400 hover:scale-110 transition duration-200"
                aria-label="Compartir en Twitter"
              >
                <FaTwitter size={30} />
              </button>
              <button
                onClick={() => handleShare("telegram")}
                className="text-blue-300 hover:scale-110 transition duration-200"
                aria-label="Compartir en Telegram"
              >
                <FaTelegram size={30} />
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

